157 // Notifies shared code that a full screen window has acquired or released
158 // a monitor
159 //
160 void _glfwInputMonitorWindow(_GLFWmonitor* monitor, _GLFWwindow* window)
161 {
162     assert(monitor != NULL);
163     monitor->window = window;
