273 // Performs lexical comparison between two @ref GLFWvidmode structures
274 //
275 int _glfwCompareVideoModes(const GLFWvidmode* fm, const GLFWvidmode* sm)
276 {
277     return compareVideoModes(fm, sm);
278 }
