69 class Sun
70 {
71 public:
72     Sun(Vector3 position, Vector3 target, Color color, bool isenabled)
73     {
74         data.DirectionalLight = CreateLight(LIGHT_DIRECTIONAL, position, target, color, Main::postProcessShader);
75         data.AmbientLight = CreateLight(LIGHT_POINT, position, target, color, Main::postProcessShader);
76         data.Position = position;
77         data.Target = target;
78         data.isEnabled = isenabled;
79 
80         Vector3 lightDirection[3] = {
81             this->data.Target, this->data.AmbientLight.target}; // Example light direction pointing upwards
82         SetShaderValue(Main::postProcessShader, this->data.DirectionalLight.targetLoc, &lightDirection[0],
83                        SHADER_UNIFORM_VEC3);
84         SetShaderValue(Main::postProcessShader, this->data.DirectionalLight.targetLoc, &lightDirection[1],
85                        SHADER_UNIFORM_VEC3);
86         // SetShaderValue(Main::postProcessShader, flashlight->GetLight().targetLoc, &lightDirection[1],
87         //                SHADER_UNIFORM_VEC3);
88     };
89 
90     void DisableLight()
91     {
92         data.isEnabled = false;
93     }
94 
95     void EnableLight()
96     {
97         data.isEnabled = true;
98     }
99     SunData data;
100 };
