96         static void StartClock()
97         {
98             previousTime = GetTime();
99             currentTime = 0.0;
100             updateDrawTime = 0.0;
101             waitTime = 0.0;
102             deltaTime = 0.0f;
103             timeCounter = 0.0f;
104             targetFPS = GetMonitorRefreshRate(GetCurrentMonitor());
105 
106             if (targetFPS == 0)
107             {
108                 throw std::runtime_error("Target FPS must be greater than 0");
109             }
110         }
111 
112         static void Update()
113         {
114             currentTime = GetTime();
115             updateDrawTime = currentTime - previousTime;
116 
117             if (targetFPS > 0)
118             {
119                 waitTime = (1.0f / static_cast<float>(targetFPS)) - updateDrawTime;
120                 if (waitTime > 0.0)
121                 {
122                     WaitTime(waitTime);
123                     currentTime = GetTime();
124                     deltaTime = static_cast<float>(currentTime - previousTime);
125                 }
126             }
127             else
128             {
129                 deltaTime = static_cast<float>(updateDrawTime);
130             }
131             previousTime = currentTime;
132         }
133 
134         static float DeltaTime()
135         {
136             return deltaTime;
137         }
138 
139     private:
140         static double previousTime;
141         static double currentTime;
142         static double updateDrawTime;
143         static double waitTime;
144         static inline float deltaTime;
145         static float timeCounter;
146         static int targetFPS;
147     };
