390         static Mesh GenMeshCustom(TriVert verts)
391         {
392             Mesh mesh = {0};
393             mesh.triangleCount = 1;
394             mesh.vertexCount = mesh.triangleCount * 3;
395             mesh.vertices = (float *)MemAlloc(mesh.vertexCount * 3 * sizeof(float));  // 3 vertices, 3 coordinates each (x, y, z)
396             mesh.texcoords = (float *)MemAlloc(mesh.vertexCount * 2 * sizeof(float)); // 3 vertices, 2 coordinates each (x, y)
397             mesh.normals = (float *)MemAlloc(mesh.vertexCount * 3 * sizeof(float));   // 3 vertices, 3 coordinates each (x, y, z)
398 
399             // Vertex at (0, 0, 0)
400             mesh.vertices[0] = verts.v1.x;
401             mesh.vertices[1] = verts.v2.x;
402             mesh.vertices[2] = verts.v3.x;
403             mesh.normals[0] = verts.v1.x;
404             mesh.normals[1] = verts.v2.x;
405             mesh.normals[2] = verts.v3.x;
406             mesh.texcoords[0] = 0;
407             mesh.texcoords[1] = 0;
408 
409             // Vertex at (1, 0, 2)
410             mesh.vertices[3] = verts.v1.y;
411             mesh.vertices[4] = verts.v2.y;
412             mesh.vertices[5] = verts.v3.y;
413             mesh.normals[3] = verts.v1.y;
414             mesh.normals[4] = verts.v2.y;
415             mesh.normals[5] = verts.v3.y;
416             mesh.texcoords[2] = 0.5f;
417             mesh.texcoords[3] = 1.0f;
418 
419             // Vertex at (2, 0, 0)
420             mesh.vertices[6] = verts.v1.z;
421             mesh.vertices[7] = verts.v2.z;
422             mesh.vertices[8] = verts.v3.z;
423             mesh.normals[6] = verts.v3.z;
424             mesh.normals[7] = verts.v3.z;
425             mesh.normals[8] = verts.v3.z;
426             mesh.texcoords[4] = 1;
427             mesh.texcoords[5] = 0;
428 
429             // Upload mesh data from CPU (RAM) to GPU (VRAM) memory
430             UploadMesh(&mesh, false);
431 
432             return mesh;
433         }
