780         static Vector3 Vector3RandomControlled(Vector3 start, int minDeviation, int maxDeviation)
781         {
782             Vector3 result = start;
783 
784             int roll = GetRandomValue(1, 3);
785             int deviationRoll = GetRandomValue(0, 1);
786 
787             if (roll == 1)
788             {
789                 result.x += (float)GetRandomValue(minDeviation, maxDeviation) + minDeviation;
790                 result.y += minDeviation;
791                 result.z += minDeviation;
792 
793                 if (deviationRoll == 1)
794                 {
795                     result.x += minDeviation;
796                 }
797                 else
798                 {
799                     result.x -= minDeviation;
800                 }
801             }
802             else if (roll == 2)
803             {
804                 result.y += (float)GetRandomValue(minDeviation, maxDeviation);
805                 result.z += minDeviation;
806 
807                 if (deviationRoll == 1)
808                 {
809                     result.y += minDeviation;
810                 }
811                 else
812                 {
813                     result.y -= minDeviation;
814                 }
815             }
816             else
817             {
818                 result.x += minDeviation;
819                 result.y += minDeviation;
820                 result.z += minDeviation;
821 
822                 if (deviationRoll == 1)
823                 {
824                     result.z += (float)GetRandomValue(minDeviation, maxDeviation);
825                 }
826                 else
827                 {
828                     result.z -= (float)GetRandomValue(minDeviation, maxDeviation);
829                 }
830             }
831 
832             return result;
833         }
834 
835         // same as DualVector3RandomEx() but only returns one value
836         static Vector3 Vector3RandomEx(Vector3 min, Vector3 max)
837         {
838             return (Vector3){(float)GetRandomValue(min.x, max.x), (float)GetRandomValue(min.y, max.y), (float)GetRandomValue(min.z, max.z)};
839         }
840     };
841 
842     // the same as Vector2 but with int
843     typedef struct
844     {
845         int x;
846         int y;
847     } intVec2;
848 
849     // the same as Vector3 but with ints
850     typedef struct
851     {
852         int x;
853         int y;
854         int z;
855     } intVec3;
856 
857     // for use where you need to have a equal reflection. So it will be both positive vector3 and negative
858     typedef struct
859     {
860         Vector3 p;
861         Vector3 n;
862     } DualVec3;
863 
864     static const BoundingBox BoxNull = {0, 0, 0, 0, 0, 0};
865 }
