4 class MarchingCubes {
5 public:
6     MarchingCubes(int width, int height, int depth, float threshold)
7         : width(width), height(height), depth(depth), threshold(threshold) {
8         // Create the 3D grid
9         grid.resize(width * height * depth);
10     }
11 
12     void generateMesh() {
13         // Generate the mesh
14         for (int z = 0; z < depth - 1; z++) {
15             for (int y = 0; y < height - 1; y++) {
16                 for (int x = 0; x < width - 1; x++) {
17                     // Compute the index into the grid
18                     int index = x + y * width + z * width * height;
19 
20                     // Retrieve the grid values
21                     float val0 = grid[index];
22                     float val1 = grid[index + 1];
23                     float val2 = grid[index + width];
24                     float val3 = grid[index + width + 1];
25                     float val4 = grid[index + width * height];
26                     float val5 = grid[index + width * height + 1];
27                     float val6 = grid[index + width * height + width];
28                     float val7 = grid[index + width * height + width + 1];
29 
30                     // Perform the Marching Cubes algorithm
31                     int cubeIndex = 0;
32                     if (val0 < threshold) cubeIndex |= 1;
33                     if (val1 < threshold) cubeIndex |= 2;
34                     if (val2 < threshold) cubeIndex |= 8;
35                     if (val3 < threshold) cubeIndex |= 4;
36                     if (val4 < threshold) cubeIndex |= 16;
37                     if (val5 < threshold) cubeIndex |= 32;
38                     if (val6 < threshold) cubeIndex |= 128;
39                     if (val7 < threshold) cubeIndex |= 64;
40 
41                     // Generate the triangles for the current cube
42                     for (int i = 0; triTable[cubeIndex][i] != -1; i += 3) {
43                         int edge0 = triTable[cubeIndex][i];
44                         int edge1 = triTable[cubeIndex][i + 1];
45                         int edge2 = triTable[cubeIndex][i + 2];
46 
47                         // Compute the interpolated positions and add them to the vertices list
48                         vertices.push_back(interpolateVertex(x, y, z, edge0));
49                         vertices.push_back(interpolateVertex(x, y, z, edge1));
50                         vertices.push_back(interpolateVertex(x, y, z, edge2));
51                     }
52                 }
53             }
54         }
55     }
56 
57     void setGridValue(int x, int y, int z, float value) {
58         // Set the value at the specified grid position
59         int index = x + y * width + z * width * height;
60         grid[index] = value;
61     }
62 
63     void render() {
64         // Render the generated mesh
65         for (size_t i = 0; i < vertices.size(); i += 3) {
66             DrawTriangle3D()(vertices[i], vertices[i + 1], vertices[i + 2], GREEN);
67         }
68     }
69 
70 private:
71     struct Vector3 {
72         float x, y, z;
73     };
74 
75     // Compute the interpolated vertex position
76     Vector3 interpolateVertex(int x, int y, int z, int edge) {
77         Vector3 p0 = { static_cast<float>(x), static_cast<float>(y), static_cast<float>(z) };
78         Vector3 p1 = { static_cast<float>(x + edgeToVertexOffset[edge][0]),
79                        static_cast<float>(y + edgeToVertexOffset[edge][1]),
80                        static_cast<float>(z + edgeToVertexOffset[edge][2]) };
81 
82         float val0 = grid[x + y * width + z * width * height];
83         float val1 = grid[x + edgeToVertexOffset[edge][0] +
84                            (y + edgeToVertexOffset[edge][1]) * width +
85                            (z + edgeToVertexOffset[edge][2]) * width * height];
86 
87         float mu = (threshold - val0) / (val1 - val0);
88 
89         Vector3 p;
90         p.x = p0.x + mu * (p1.x - p0.x);
91         p.y = p0.y + mu * (p1.y - p0.y);
92         p.z = p0.z + mu * (p1.z - p0.z);
93 
94         return p;
95     }
