380 int main()
381 {
382     const int screenWidth = 800;
383     const int screenHeight = 600;
384 
385     InitWindow(screenWidth, screenHeight, "Marching Cubes");
386 
387     Camera3D camera;
388     camera.position = Vector3(10.0f, 10.0f, 10.0f);
389     camera.target = Vector3(0.0f, 0.0f, 0.0f);
390     camera.up = Vector3(0.0f, 1.0f, 0.0f);
391     camera.fovy = 45.0f;
392     camera.type = CAMERA_PERSPECTIVE;
393 
394     SetCameraMode(camera, CAMERA_ORBITAL);
395 
396     MarchingCubes marchingCubes(64, 64, 64, 0.5f);
397     std::vector<float> data(64 * 64 * 64);
398 
399     // Generate some sample data
400     for (int z = 0; z < 64; ++z)
401     {
402         for (int y = 0; y < 64; ++y)
403         {
404             for (int x = 0; x < 64; ++x)
405             {
406                 float dx = static_cast<float>(x) - 32.0f;
407                 float dy = static_cast<float>(y) - 32.0f;
408                 float dz = static_cast<float>(z) - 32.0f;
409                 data[marchingCubes.Index(x, y, z)] = std::sqrt(dx * dx + dy * dy + dz * dz);
410             }
411         }
412     }
413 
414     SetTargetFPS(60);
415 
416     while (!WindowShouldClose())
417     {
418         UpdateCamera(&camera);
419 
420         if (IsKeyPressed(KEY_SPACE))
421         {
422             marchingCubes.GenerateSurface(data.data());
423         }
424 
425         BeginDrawing();
426         ClearBackground(RAYWHITE);
427 
428         BeginMode3D(camera);
429 
430         // Draw the generated surface
431         DrawMesh(DrawMeshPoly(marchingCubes.GetSurface(), WHITE));
432 
433         // Draw the grid lines
434         for (int x = -32; x <= 32; ++x)
435         {
436             DrawLine3D(Vector3(static_cast<float>(x), -32.0f, 0.0f), Vector3(static_cast<float>(x), 32.0f, 0.0f), GRAY);
437             DrawLine3D(Vector3(static_cast<float>(x), 0.0f, -32.0f), Vector3(static_cast<float>(x), 0.0f, 32.0f), GRAY);
438         }
439 
440         for (int z = -32; z <= 32; ++z) 
441         {
442             DrawLine3D(Vector3(-32.0f, 0.0f, static_cast<float>(z)), Vector3(32.0f, 0.0f, static_cast<float>(z)), GRAY);
443             DrawLine3D(Vector3(0.0f, -32.0f, static_cast<float>(z)), Vector3(0.0f, 32.0f, static_cast<float>(z)), GRAY);
444         }
445 
446         EndMode3D();
447 
448         DrawFPS(10, 10);
449 
450         EndDrawing();
451     }
452 
453     CloseWindow();
454 
455     return 0;
456 }
