42 class ButtonR
43 {
44 
45 public:
46     Font font = GetFontDefault();
47     float fontSize = 32,
48           fontSpacing = 3.0f;
49     Color fontColor = BLACK;
50     bool isRayFont = true;
51 
52     //---- Text
53     string text = "Button";
54     Vector2 textSize = MeasureTextEx(font, text.c_str(), fontSize, fontSpacing),
55             oneCharSize = MeasureTextEx(font, "C", fontSize, fontSpacing);
56 
57     //--- Button position, size, color
58     /*
59         The button size is computed from the font size and length of the text
60         See mutators to modify the button’s position,
61         the text’s position in the button, and the button’s size.
62     */
63     bool txtResizeBtn = true;
64     float btnWidth = (textSize.x + 3.5f * oneCharSize.x),
65           btnHeight = textSize.y * (float)(2.0f * (textSize.y / fontSize));
66     Rectangle rect = {100, 100, btnWidth, btnHeight};
67     
68     // colors
69     Color btnLiveColor = {222, 214, 202, 255},
70           // Botton state
71         btnHover = {135, 195, 74, 100},
72           btnPressed = {66, 165, 245, 100},
73           btnIdle = btnLiveColor; // idle state
74 
75     //---- Button border  position, size, color, thickness
76     /*
77         The boder size is computed from the font size and length of the text
78         modifying the button's size, we also modify the border's size.
79         See mutators for modifying specifically the shadow's size.
80     */
81     Rectangle border{rect.x, rect.y, btnWidth, btnHeight};
82     // color
83     Color borderLiveColor = BLACK,
84           // Botton state
85         borderHover = {57, 73, 171, 100},
86           borderPressed = {0, 105, 92, 100},
87           borderIdle = borderLiveColor;
88     float borderThickness = (rect.width + rect.height) / 150;
89     bool isBorder = true;
90 
91     //---- Shaddow
92     /*
93         The shadow size is computed from the font size andlength of the text
94         modifying the button's size, we also modify the shadow's size.
95         See mutators for modifying specifically the shadow's size and position.
96     */
97     double shadowOffset = 0.05;
98     Rectangle shadow{rect.x + 5, rect.y + 5, btnWidth, btnHeight};
99     Color shadowColor = GRAY;
100     bool isShadow = true;
101 
102     //--- Centers text in button
103     Vector2 textPos =
104         {
105             rect.x + (rect.width - textSize.x) / 2,
106             rect.y + (rect.height - textSize.y) / 2};
107 
108     ButtonR(string text, float x, float y)
109     {
110         this->text = text;
111         this->rect.x = x;
112         this->rect.y = y;
113 
114         buildBtn();
115     }
116 
117 
118     void draw()
119     {
120         update();
121 
122         if (isShadow)
123             DrawRectangleRec(shadow, shadowColor);
124         DrawRectangleRec(rect, btnLiveColor);
125         if (isBorder)
126             DrawRectangleLinesEx(border, borderThickness, borderLiveColor);
127         DrawTextEx(font, text.c_str(), textPos, fontSize, fontSpacing, fontColor);
128     }
129 
130     int update()
131     {
132         int result = -1;
133 
134         // Mouse position
135         Vector2 mouse_pos = GetMousePosition();
136 
137         if (CheckCollisionPointRec(mouse_pos, rect))
138         {
139             if (IsMouseButtonDown(MOUSE_BUTTON_LEFT))
140             {
141                 btnLiveColor = btnPressed;
142                 borderLiveColor = borderPressed;
143                 result = MOUSE_BUTTON_LEFT;
144             }
145             else if (IsMouseButtonDown(MOUSE_BUTTON_RIGHT))
146             {
147                 btnLiveColor = btnPressed;
148                 borderLiveColor = borderPressed;
149                 result = MOUSE_BUTTON_RIGHT;
150             }
151             else if (IsMouseButtonDown(MOUSE_BUTTON_MIDDLE))
152             {
153                 btnLiveColor = btnPressed;
154                 borderLiveColor = borderPressed;
155                 result = MOUSE_BUTTON_MIDDLE;
156             }
157             else
158             {
159                 btnLiveColor = btnHover;
160                 borderLiveColor = borderHover;
161             }
162         }
163         else
164         {
165             // state idle
166             btnLiveColor = btnIdle;
167             borderLiveColor = borderIdle;
168         }
169 
170         return result;
171 
172     } // update()
173 
174     void buildBtn()
175     {
176         //--- Button size
177         /*
178             The button size is computed from the font size and length of the text
179             See mutators to modify the button’s position,
180             the text’s position in the button, and the button’s size.
181         */
182         float ratioWidth = (isRayFont) ? 3.5f : 4.0f,
183               ratioHeight = (isRayFont) ? 2.0f : 1.4f;
184         textSize = MeasureTextEx(font, text.c_str(), fontSize, fontSpacing);
185         oneCharSize = MeasureTextEx(font, "C", fontSize, fontSpacing);
186 
187         if (txtResizeBtn)
188         {
189             //--- Button size
190             rect.width = (textSize.x + ratioWidth * oneCharSize.x);
191             rect.height = textSize.y * (float)(ratioHeight * (textSize.y / fontSize));
192         }
193 
194         //--- Centers text in button
195         textPos =
196             {
197                 rect.x + (rect.width - textSize.x) / 2,
198                 rect.y + (rect.height - textSize.y) / 2};
199 
200         //---- Button border
201         border = rect;
202         borderThickness = (rect.width + rect.height) / 150;
203 
204         //---- Button shadow
205         shadow = {rect.x + 5, rect.y + 5, rect.width, rect.height};
206 
207     } // build_btn()
208 
