240         textPos.y = y;
241     }
242 
243     void setBtnPosition(float btnX, float btnY)
244     {
245         rect.x = btnX;
246         rect.y = btnY;
247         buildBtn();
248     }
249 
250     void setBtnSize(float btnWidth, float btnHeight)
251     {
252         rect.width = btnWidth;
253         rect.height = btnHeight;
254         txtResizeBtn = false;
255         buildBtn();
256     }
