258     bool IsClicked()
259     {
260         int result = update();
261 
262         Vector2 Mouse = GetMousePosition();
263         this->position.x = this->rect.x;
264         this->position.y = this->rect.y;
265 
266 
267         Vector2 Boundries = {position.x + rect.width,
268                              position.y + rect.height
269         };
270         
271         int Xmargin = rect.width + rect.x;
272         int YMargin = rect.height + rect.y;
273 
274         int MaxMarginX = Vector2Distance(Vector2Zero(), this->position);
275         int MaxMarginY = Vector2Distance(Vector2Zero(), this->position);
276 
277 
278         
279 
280 
281 
282         const int ButtonRadiusX = rect.x + rect.width / 2;
283         const int ButtonRadiusY = rect.y + rect.height / 2;
284         
285         const int MinButtonRadiusX = position.x - (rect.width / 2);
286         const int MinButtonRadiusY = position.y - (rect.height / 2);
287 
288 
289         float MouseDistanceFromButtonCenter = Vector2Distance(position, Mouse);
290 
291         if (Mouse.x >= MinButtonRadiusX && Mouse.x <= ButtonRadiusX && Mouse.y >= MinButtonRadiusY && Mouse.y <= ButtonRadiusY &&  IsMouseButtonPressed(MOUSE_BUTTON_LEFT)) {
292             return true;
293         } else
294         {
295             return false;
296         }
297         return false;
298     }
