7     template<typename T>
8     GLM_FUNC_QUALIFIER mat<4, 4, T, defaultp> eulerAngleX
9     (
10         T const& angleX
11     )
12     {
13         T cosX = glm::cos(angleX);
14         T sinX = glm::sin(angleX);
15 
16         return mat<4, 4, T, defaultp>(
17             T(1), T(0), T(0), T(0),
18             T(0), cosX, sinX, T(0),
19             T(0),-sinX, cosX, T(0),
20             T(0), T(0), T(0), T(1));
21     }
