24     GLM_FUNC_QUALIFIER mat<4, 4, T, defaultp> eulerAngleY
25     (
26         T const& angleY
27     )
28     {
29         T cosY = glm::cos(angleY);
30         T sinY = glm::sin(angleY);
31 
32         return mat<4, 4, T, defaultp>(
33             cosY,   T(0),   -sinY,  T(0),
34             T(0),   T(1),   T(0),   T(0),
35             sinY,   T(0),   cosY,   T(0),
36             T(0),   T(0),   T(0),   T(1));
37     }
