55     template <typename T>
56     GLM_FUNC_QUALIFIER mat<4, 4, T, defaultp> derivedEulerAngleX
57     (
58         T const & angleX,
59         T const & angularVelocityX
60     )
61     {
62         T cosX = glm::cos(angleX) * angularVelocityX;
63         T sinX = glm::sin(angleX) * angularVelocityX;
64 
65         return mat<4, 4, T, defaultp>(
66             T(0), T(0), T(0), T(0),
67             T(0),-sinX, cosX, T(0),
68             T(0),-cosX,-sinX, T(0),
69             T(0), T(0), T(0), T(0));
70     }
