125     template<typename T>
126     GLM_FUNC_QUALIFIER mat<4, 4, T, defaultp> eulerAngleYX
127     (
128         T const& angleY,
129         T const& angleX
130     )
131     {
132         T cosX = glm::cos(angleX);
133         T sinX = glm::sin(angleX);
134         T cosY = glm::cos(angleY);
135         T sinY = glm::sin(angleY);
136 
137         return mat<4, 4, T, defaultp>(
138             cosY,          0,      -sinY,    T(0),
139             sinY * sinX,  cosX, cosY * sinX, T(0),
140             sinY * cosX, -sinX, cosY * cosX, T(0),
141             T(0),         T(0),     T(0),    T(1));
142     }
