370         T s2 = glm::sin(t2);
371         T c3 = glm::cos(t3);
372         T s3 = glm::sin(t3);
373 
374         mat<4, 4, T, defaultp> Result;
375         Result[0][0] = c1 * c2 * c3 - s1 * s3;
376         Result[0][1] = c3 * s2;
