140 PB_DS_CLASS_T_DEC
141 void
142 PB_DS_CLASS_C_DEC::
143 bubble_to_top(node_pointer p_nd)
144 {
145   node_pointer p_parent = parent(p_nd);
146   while (p_parent != 0)
147     {
148       swap_with_parent(p_nd, p_parent);
149       p_parent = parent(p_nd);
150     }
151 }
