705 #ifndef CGLTF_MALLOC
706 #define CGLTF_MALLOC(size) malloc(size)
707 #endif
708 #ifndef CGLTF_FREE
709 #define CGLTF_FREE(ptr) free(ptr)
710 #endif
711 #ifndef CGLTF_ATOI
712 #define CGLTF_ATOI(str) atoi(str)
713 #endif
