2616 static int cgltf_parse_json_texture_transform(jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_texture_transform* out_texture_transform)
2617 {
2618     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2619 
