2641         else if (cgltf_json_strcmp(tokens + i, json_chunk, "texCoord") == 0)
2642         {
2643             ++i;
2644             out_texture_transform->texcoord = cgltf_json_to_int(tokens + i, json_chunk);
2645             ++i;
2646         }
2647         else
2648         {
2649             i = cgltf_skip_json(tokens, i + 1);
2650         }
2651 
2652         if (i < 0)
