386 static int memcompare( unsigned char * buf1, unsigned char * buf2, unsigned int size )
387 {
388     unsigned int i;
389 
390     i = 0;
391 
392     while(i<size)
393     {
394         if(buf1[i] != buf2[i])
395         {
396             return 0;
397         }
398         i++;
399     }
400 
401     return 1;
402 }
