404 static int getnote( jar_mod_context_t * mod, unsigned short period, int finetune )
405 {
406     int i;
407 
408     for(i = 0; i < FULL_PERIOD_TABLE_LENGTH; i++)
409     {
410         if(period >= mod->fullperiod[i])
411         {
412             return i;
413         }
414     }
415 
416     return MAXNOTES;
417 }
