2584         fprintf(stderr, __VA_ARGS__); \
2585         fflush(stderr); \
2586     } while(0)
2587 
2588 #define DEBUG_ERR(...) do {      \
2589         fprintf(stderr, __VA_ARGS__); \
2590         fflush(stderr); \
2591     } while(0)
2592 
2593 #define FATAL(...) do {      \
2594         fprintf(stderr, __VA_ARGS__); \
2595         fflush(stderr); \
2596         exit(1); \
2597     } while(0)
2598 
2599 #define FATAL_ERR(...) do {      \
2600         fprintf(stderr, __VA_ARGS__); \
2601         fflush(stderr); \
2602         exit(1); \
2603     } while(0)
