885 /*
886  * -- jsmn.h start --
887  * Source: https://github.com/zserge/jsmn
888  * License: MIT
889  */
890 typedef enum {
891     JSMN_UNDEFINED = 0,
892     JSMN_OBJECT = 1,
893     JSMN_ARRAY = 2,
894     JSMN_STRING = 3,
895     JSMN_PRIMITIVE = 4
896 } jsmntype_t;
897 enum jsmnerr {
898     /* Not enough tokens were provided */
899     JSMN_ERROR_NOMEM = -1,
900     /* Invalid character inside JSON string */
901     JSMN_ERROR_INVAL = -2,
902     /* The string is not a full JSON packet, more bytes expected */
903     JSMN_ERROR_PART = -3
904 };
905 typedef struct {
906     jsmntype_t type;
907     ptrdiff_t start;
908     ptrdiff_t end;
909     int size;
910 #ifdef JSMN_PARENT_LINKS
911     int parent;
912 #endif
913 } jsmntok_t;
914 typedef struct {
915     size_t pos; /* offset in the JSON string */
916     unsigned int toknext; /* next token to allocate */
917     int toksuper; /* superior token node, e.g parent object or array */
918 } jsmn_parser;
919 static void jsmn_init(jsmn_parser *parser);
920 static int jsmn_parse(jsmn_parser *parser, const char *js, size_t len, jsmntok_t *tokens, size_t num_tokens);
921 /*
922  * -- jsmn.h end --
923  */
