936 #ifndef CGLTF_MALLOC
937 #define CGLTF_MALLOC(size) malloc(size)
938 #endif
939 #ifndef CGLTF_FREE
940 #define CGLTF_FREE(ptr) free(ptr)
941 #endif
942 #ifndef CGLTF_ATOI
943 #define CGLTF_ATOI(str) atoi(str)
944 #endif
945 #ifndef CGLTF_ATOF
946 #define CGLTF_ATOF(str) atof(str)
947 #endif
948 #ifndef CGLTF_ATOLL
949 #define CGLTF_ATOLL(str) atoll(str)
950 #endif
951 #ifndef CGLTF_VALIDATE_ENABLE_ASSERTS
952 #define CGLTF_VALIDATE_ENABLE_ASSERTS 0
953 #endif
