955 static void* cgltf_default_alloc(void* user, cgltf_size size)
956 {
957     (void)user;
958     return CGLTF_MALLOC(size);
959 }
960 
961 static void cgltf_default_free(void* user, void* ptr)
962 {
963     (void)user;
964     CGLTF_FREE(ptr);
965 }
966 
967 static void* cgltf_calloc(cgltf_options* options, size_t element_size, cgltf_size count)
968 {
969     if (SIZE_MAX / element_size < count)
970     {
971         return NULL;
972     }
973     void* result = options->memory.alloc_func(options->memory.user_data, element_size * count);
974     if (!result)
975     {
976         return NULL;
977     }
978     memset(result, 0, element_size * count);
979     return result;
980 }
981 
982 static cgltf_result cgltf_default_file_read(const struct cgltf_memory_options* memory_options, const struct cgltf_file_options* file_options, const char* path, cgltf_size* size, void** data)
983 {
984     (void)file_options;
985     void* (*memory_alloc)(void*, cgltf_size) = memory_options->alloc_func ? memory_options->alloc_func : &cgltf_default_alloc;
986     void (*memory_free)(void*, void*) = memory_options->free_func ? memory_options->free_func : &cgltf_default_free;
987 
988     FILE* file = fopen(path, "rb");
989     if (!file)
990     {
991         return cgltf_result_file_not_found;
992     }
993 
994     cgltf_size file_size = size ? *size : 0;
995 
996     if (file_size == 0)
997     {
998         fseek(file, 0, SEEK_END);
999 
1000 #ifdef _WIN32
1001         __int64 length = _ftelli64(file);
1002 #else
1003         long length = ftell(file);
1004 #endif
1005 
1006         if (length < 0)
1007         {
1008             fclose(file);
1009             return cgltf_result_io_error;
1010         }
1011 
1012         fseek(file, 0, SEEK_SET);
1013         file_size = (cgltf_size)length;
1014     }
1015 
1016     char* file_data = (char*)memory_alloc(memory_options->user_data, file_size);
1017     if (!file_data)
1018     {
1019         fclose(file);
1020         return cgltf_result_out_of_memory;
1021     }
1022     
1023     cgltf_size read_size = fread(file_data, 1, file_size, file);
1024 
1025     fclose(file);
1026 
1027     if (read_size != file_size)
1028     {
1029         memory_free(memory_options->user_data, file_data);
1030         return cgltf_result_io_error;
1031     }
1032 
1033     if (size)
1034     {
1035         *size = file_size;
1036     }
1037     if (data)
1038     {
1039         *data = file_data;
1040     }
1041 
1042     return cgltf_result_success;
1043 }
1044 
1045 static void cgltf_default_file_release(const struct cgltf_memory_options* memory_options, const struct cgltf_file_options* file_options, void* data)
1046 {
1047     (void)file_options;
1048     void (*memfree)(void*, void*) = memory_options->free_func ? memory_options->free_func : &cgltf_default_free;
1049     memfree(memory_options->user_data, data);
1050 }
1051 
1052 static cgltf_result cgltf_parse_json(cgltf_options* options, const uint8_t* json_chunk, cgltf_size size, cgltf_data** out_data);
1053 
1054 cgltf_result cgltf_parse(const cgltf_options* options, const void* data, cgltf_size size, cgltf_data** out_data)
1055 {
1056     if (size < GlbHeaderSize)
1057     {
1058         return cgltf_result_data_too_short;
1059     }
1060 
1061     if (options == NULL)
1062     {
1063         return cgltf_result_invalid_options;
1064     }
1065 
1066     cgltf_options fixed_options = *options;
1067     if (fixed_options.memory.alloc_func == NULL)
1068     {
1069         fixed_options.memory.alloc_func = &cgltf_default_alloc;
1070     }
1071     if (fixed_options.memory.free_func == NULL)
1072     {
1073         fixed_options.memory.free_func = &cgltf_default_free;
1074     }
1075 
1076     uint32_t tmp;
1077     // Magic
1078     memcpy(&tmp, data, 4);
1079     if (tmp != GlbMagic)
1080     {
1081         if (fixed_options.type == cgltf_file_type_invalid)
1082         {
1083             fixed_options.type = cgltf_file_type_gltf;
1084         }
1085         else if (fixed_options.type == cgltf_file_type_glb)
1086         {
1087             return cgltf_result_unknown_format;
1088         }
1089     }
1090 
1091     if (fixed_options.type == cgltf_file_type_gltf)
1092     {
1093         cgltf_result json_result = cgltf_parse_json(&fixed_options, (const uint8_t*)data, size, out_data);
1094         if (json_result != cgltf_result_success)
1095         {
1096             return json_result;
1097         }
1098 
1099         (*out_data)->file_type = cgltf_file_type_gltf;
1100 
1101         return cgltf_result_success;
1102     }
1103 
1104     const uint8_t* ptr = (const uint8_t*)data;
1105     // Version
1106     memcpy(&tmp, ptr + 4, 4);
1107     uint32_t version = tmp;
1108     if (version != GlbVersion)
1109     {
1110         return version < GlbVersion ? cgltf_result_legacy_gltf : cgltf_result_unknown_format;
1111     }
1112 
1113     // Total length
1114     memcpy(&tmp, ptr + 8, 4);
1115     if (tmp > size)
1116     {
1117         return cgltf_result_data_too_short;
1118     }
1119 
1120     const uint8_t* json_chunk = ptr + GlbHeaderSize;
1121 
1122     if (GlbHeaderSize + GlbChunkHeaderSize > size)
1123     {
1124         return cgltf_result_data_too_short;
1125     }
1126 
1127     // JSON chunk: length
1128     uint32_t json_length;
1129     memcpy(&json_length, json_chunk, 4);
1130     if (GlbHeaderSize + GlbChunkHeaderSize + json_length > size)
1131     {
1132         return cgltf_result_data_too_short;
1133     }
1134 
1135     // JSON chunk: magic
1136     memcpy(&tmp, json_chunk + 4, 4);
1137     if (tmp != GlbMagicJsonChunk)
1138     {
1139         return cgltf_result_unknown_format;
1140     }
1141 
1142     json_chunk += GlbChunkHeaderSize;
1143 
1144     const void* bin = 0;
1145     cgltf_size bin_size = 0;
1146 
1147     if (GlbHeaderSize + GlbChunkHeaderSize + json_length + GlbChunkHeaderSize <= size)
1148     {
1149         // We can read another chunk
1150         const uint8_t* bin_chunk = json_chunk + json_length;
1151 
1152         // Bin chunk: length
1153         uint32_t bin_length;
1154         memcpy(&bin_length, bin_chunk, 4);
1155         if (GlbHeaderSize + GlbChunkHeaderSize + json_length + GlbChunkHeaderSize + bin_length > size)
1156         {
1157             return cgltf_result_data_too_short;
1158         }
1159 
1160         // Bin chunk: magic
1161         memcpy(&tmp, bin_chunk + 4, 4);
1162         if (tmp != GlbMagicBinChunk)
1163         {
1164             return cgltf_result_unknown_format;
1165         }
1166 
1167         bin_chunk += GlbChunkHeaderSize;
1168 
1169         bin = bin_chunk;
1170         bin_size = bin_length;
1171     }
1172 
1173     cgltf_result json_result = cgltf_parse_json(&fixed_options, json_chunk, json_length, out_data);
1174     if (json_result != cgltf_result_success)
1175     {
1176         return json_result;
1177     }
1178 
1179     (*out_data)->file_type = cgltf_file_type_glb;
1180     (*out_data)->bin = bin;
1181     (*out_data)->bin_size = bin_size;
1182 
1183     return cgltf_result_success;
1184 }
1185 
1186 cgltf_result cgltf_parse_file(const cgltf_options* options, const char* path, cgltf_data** out_data)
1187 {
1188     if (options == NULL)
1189     {
1190         return cgltf_result_invalid_options;
1191     }
1192 
1193     cgltf_result (*file_read)(const struct cgltf_memory_options*, const struct cgltf_file_options*, const char*, cgltf_size*, void**) = options->file.read ? options->file.read : &cgltf_default_file_read;
1194     void (*file_release)(const struct cgltf_memory_options*, const struct cgltf_file_options*, void* data) = options->file.release ? options->file.release : cgltf_default_file_release;
1195 
1196     void* file_data = NULL;
1197     cgltf_size file_size = 0;
1198     cgltf_result result = file_read(&options->memory, &options->file, path, &file_size, &file_data);
1199     if (result != cgltf_result_success)
1200     {
1201         return result;
1202     }
1203 
1204     result = cgltf_parse(options, file_data, file_size, out_data);
1205 
1206     if (result != cgltf_result_success)
1207     {
1208         file_release(&options->memory, &options->file, file_data);
1209         return result;
1210     }
1211 
1212     (*out_data)->file_data = file_data;
1213 
1214     return cgltf_result_success;
1215 }
1216 
1217 static void cgltf_combine_paths(char* path, const char* base, const char* uri)
1218 {
1219     const char* s0 = strrchr(base, '/');
1220     const char* s1 = strrchr(base, '\\');
1221     const char* slash = s0 ? (s1 && s1 > s0 ? s1 : s0) : s1;
1222 
1223     if (slash)
1224     {
1225         size_t prefix = slash - base + 1;
1226 
1227         strncpy(path, base, prefix);
1228         strcpy(path + prefix, uri);
1229     }
1230     else
1231     {
1232         strcpy(path, uri);
1233     }
1234 }
1235 
1236 static cgltf_result cgltf_load_buffer_file(const cgltf_options* options, cgltf_size size, const char* uri, const char* gltf_path, void** out_data)
1237 {
1238     void* (*memory_alloc)(void*, cgltf_size) = options->memory.alloc_func ? options->memory.alloc_func : &cgltf_default_alloc;
1239     void (*memory_free)(void*, void*) = options->memory.free_func ? options->memory.free_func : &cgltf_default_free;
1240     cgltf_result (*file_read)(const struct cgltf_memory_options*, const struct cgltf_file_options*, const char*, cgltf_size*, void**) = options->file.read ? options->file.read : &cgltf_default_file_read;
1241 
1242     char* path = (char*)memory_alloc(options->memory.user_data, strlen(uri) + strlen(gltf_path) + 1);
1243     if (!path)
1244     {
1245         return cgltf_result_out_of_memory;
1246     }
1247 
1248     cgltf_combine_paths(path, gltf_path, uri);
1249 
1250     // after combining, the tail of the resulting path is a uri; decode_uri converts it into path
1251     cgltf_decode_uri(path + strlen(path) - strlen(uri));
1252 
1253     void* file_data = NULL;
1254     cgltf_result result = file_read(&options->memory, &options->file, path, &size, &file_data);
1255 
1256     memory_free(options->memory.user_data, path);
1257 
1258     *out_data = (result == cgltf_result_success) ? file_data : NULL;
1259 
1260     return result;
1261 }
1262 
1263 cgltf_result cgltf_load_buffer_base64(const cgltf_options* options, cgltf_size size, const char* base64, void** out_data)
1264 {
1265     void* (*memory_alloc)(void*, cgltf_size) = options->memory.alloc_func ? options->memory.alloc_func : &cgltf_default_alloc;
1266     void (*memory_free)(void*, void*) = options->memory.free_func ? options->memory.free_func : &cgltf_default_free;
1267 
1268     unsigned char* data = (unsigned char*)memory_alloc(options->memory.user_data, size);
1269     if (!data)
1270     {
1271         return cgltf_result_out_of_memory;
1272     }
1273 
1274     unsigned int buffer = 0;
1275     unsigned int buffer_bits = 0;
1276 
1277     for (cgltf_size i = 0; i < size; ++i)
1278     {
1279         while (buffer_bits < 8)
1280         {
1281             char ch = *base64++;
1282 
1283             int index =
1284                 (unsigned)(ch - 'A') < 26 ? (ch - 'A') :
1285                 (unsigned)(ch - 'a') < 26 ? (ch - 'a') + 26 :
1286                 (unsigned)(ch - '0') < 10 ? (ch - '0') + 52 :
1287                 ch == '+' ? 62 :
1288                 ch == '/' ? 63 :
1289                 -1;
1290 
1291             if (index < 0)
1292             {
1293                 memory_free(options->memory.user_data, data);
1294                 return cgltf_result_io_error;
1295             }
1296 
1297             buffer = (buffer << 6) | index;
1298             buffer_bits += 6;
1299         }
1300 
1301         data[i] = (unsigned char)(buffer >> (buffer_bits - 8));
1302         buffer_bits -= 8;
1303     }
1304 
1305     *out_data = data;
1306 
1307     return cgltf_result_success;
1308 }
1309 
1310 static int cgltf_unhex(char ch)
1311 {
1312     return
1313         (unsigned)(ch - '0') < 10 ? (ch - '0') :
1314         (unsigned)(ch - 'A') < 6 ? (ch - 'A') + 10 :
1315         (unsigned)(ch - 'a') < 6 ? (ch - 'a') + 10 :
1316         -1;
1317 }
1318 
1319 cgltf_size cgltf_decode_string(char* string)
1320 {
1321     char* read = string + strcspn(string, "\\");
1322     if (*read == 0)
1323     {
1324         return read - string;
1325     }
1326     char* write = string;
1327     char* last = string;
1328 
1329     for (;;)
1330     {
1331         // Copy characters since last escaped sequence
1332         cgltf_size written = read - last;
1333         memmove(write, last, written);
1334         write += written;
1335 
1336         if (*read++ == 0)
1337         {
1338             break;
1339         }
1340 
1341         // jsmn already checked that all escape sequences are valid
1342         switch (*read++)
1343         {
1344         case '\"': *write++ = '\"'; break;
1345         case '/':  *write++ = '/';  break;
1346         case '\\': *write++ = '\\'; break;
1347         case 'b':  *write++ = '\b'; break;
1348         case 'f':  *write++ = '\f'; break;
1349         case 'r':  *write++ = '\r'; break;
1350         case 'n':  *write++ = '\n'; break;
1351         case 't':  *write++ = '\t'; break;
1352         case 'u':
1353         {
1354             // UCS-2 codepoint \uXXXX to UTF-8
1355             int character = 0;
1356             for (cgltf_size i = 0; i < 4; ++i)
1357             {
1358                 character = (character << 4) + cgltf_unhex(*read++);
1359             }
1360 
1361             if (character <= 0x7F)
1362             {
1363                 *write++ = character & 0xFF;
1364             }
1365             else if (character <= 0x7FF)
1366             {
1367                 *write++ = 0xC0 | ((character >> 6) & 0xFF);
1368                 *write++ = 0x80 | (character & 0x3F);
1369             }
1370             else
1371             {
1372                 *write++ = 0xE0 | ((character >> 12) & 0xFF);
1373                 *write++ = 0x80 | ((character >> 6) & 0x3F);
1374                 *write++ = 0x80 | (character & 0x3F);
1375             }
1376             break;
1377         }
1378         default:
1379             break;
1380         }
1381 
1382         last = read;
1383         read += strcspn(read, "\\");
1384     }
1385 
1386     *write = 0;
1387     return write - string;
1388 }
1389 
1390 cgltf_size cgltf_decode_uri(char* uri)
1391 {
1392     char* write = uri;
1393     char* i = uri;
1394 
1395     while (*i)
1396     {
1397         if (*i == '%')
1398         {
1399             int ch1 = cgltf_unhex(i[1]);
1400 
1401             if (ch1 >= 0)
1402             {
1403                 int ch2 = cgltf_unhex(i[2]);
1404 
1405                 if (ch2 >= 0)
1406                 {
1407                     *write++ = (char)(ch1 * 16 + ch2);
1408                     i += 3;
1409                     continue;
1410                 }
1411             }
1412         }
1413 
1414         *write++ = *i++;
1415     }
1416 
1417     *write = 0;
1418     return write - uri;
1419 }
1420 
1421 cgltf_result cgltf_load_buffers(const cgltf_options* options, cgltf_data* data, const char* gltf_path)
1422 {
1423     if (options == NULL)
1424     {
1425         return cgltf_result_invalid_options;
1426     }
1427 
1428     if (data->buffers_count && data->buffers[0].data == NULL && data->buffers[0].uri == NULL && data->bin)
1429     {
1430         if (data->bin_size < data->buffers[0].size)
1431         {
1432             return cgltf_result_data_too_short;
1433         }
1434 
1435         data->buffers[0].data = (void*)data->bin;
1436         data->buffers[0].data_free_method = cgltf_data_free_method_none;
1437     }
1438 
1439     for (cgltf_size i = 0; i < data->buffers_count; ++i)
1440     {
1441         if (data->buffers[i].data)
1442         {
1443             continue;
1444         }
1445 
1446         const char* uri = data->buffers[i].uri;
1447 
1448         if (uri == NULL)
1449         {
1450             continue;
1451         }
1452 
1453         if (strncmp(uri, "data:", 5) == 0)
1454         {
1455             const char* comma = strchr(uri, ',');
1456 
1457             if (comma && comma - uri >= 7 && strncmp(comma - 7, ";base64", 7) == 0)
1458             {
1459                 cgltf_result res = cgltf_load_buffer_base64(options, data->buffers[i].size, comma + 1, &data->buffers[i].data);
1460                 data->buffers[i].data_free_method = cgltf_data_free_method_memory_free;
1461 
1462                 if (res != cgltf_result_success)
1463                 {
1464                     return res;
1465                 }
1466             }
1467             else
1468             {
1469                 return cgltf_result_unknown_format;
1470             }
1471         }
1472         else if (strstr(uri, "://") == NULL && gltf_path)
1473         {
1474             cgltf_result res = cgltf_load_buffer_file(options, data->buffers[i].size, uri, gltf_path, &data->buffers[i].data);
1475             data->buffers[i].data_free_method = cgltf_data_free_method_file_release;
1476 
1477             if (res != cgltf_result_success)
1478             {
1479                 return res;
1480             }
1481         }
1482         else
1483         {
1484             return cgltf_result_unknown_format;
1485         }
1486     }
1487 
1488     return cgltf_result_success;
1489 }
1490 
1491 static cgltf_size cgltf_calc_size(cgltf_type type, cgltf_component_type component_type);
1492 
1493 static cgltf_size cgltf_calc_index_bound(cgltf_buffer_view* buffer_view, cgltf_size offset, cgltf_component_type component_type, cgltf_size count)
1494 {
1495     char* data = (char*)buffer_view->buffer->data + offset + buffer_view->offset;
1496     cgltf_size bound = 0;
1497 
1498     switch (component_type)
1499     {
1500     case cgltf_component_type_r_8u:
1501         for (size_t i = 0; i < count; ++i)
1502         {
1503             cgltf_size v = ((unsigned char*)data)[i];
1504             bound = bound > v ? bound : v;
1505         }
1506         break;
1507 
1508     case cgltf_component_type_r_16u:
1509         for (size_t i = 0; i < count; ++i)
1510         {
1511             cgltf_size v = ((unsigned short*)data)[i];
1512             bound = bound > v ? bound : v;
1513         }
1514         break;
1515 
1516     case cgltf_component_type_r_32u:
1517         for (size_t i = 0; i < count; ++i)
1518         {
1519             cgltf_size v = ((unsigned int*)data)[i];
1520             bound = bound > v ? bound : v;
1521         }
1522         break;
1523 
1524     default:
1525         ;
1526     }
1527 
1528     return bound;
1529 }
1530 
1531 #if CGLTF_VALIDATE_ENABLE_ASSERTS
1532 #define CGLTF_ASSERT_IF(cond, result) assert(!(cond)); if (cond) return result;
1533 #else
1534 #define CGLTF_ASSERT_IF(cond, result) if (cond) return result;
1535 #endif
1536 
1537 cgltf_result cgltf_validate(cgltf_data* data)
1538 {
1539     for (cgltf_size i = 0; i < data->accessors_count; ++i)
1540     {
1541         cgltf_accessor* accessor = &data->accessors[i];
1542 
1543         cgltf_size element_size = cgltf_calc_size(accessor->type, accessor->component_type);
1544 
1545         if (accessor->buffer_view)
1546         {
1547             cgltf_size req_size = accessor->offset + accessor->stride * (accessor->count - 1) + element_size;
1548 
1549             CGLTF_ASSERT_IF(accessor->buffer_view->size < req_size, cgltf_result_data_too_short);
1550         }
1551 
1552         if (accessor->is_sparse)
1553         {
1554             cgltf_accessor_sparse* sparse = &accessor->sparse;
1555 
1556             cgltf_size indices_component_size = cgltf_calc_size(cgltf_type_scalar, sparse->indices_component_type);
1557             cgltf_size indices_req_size = sparse->indices_byte_offset + indices_component_size * sparse->count;
1558             cgltf_size values_req_size = sparse->values_byte_offset + element_size * sparse->count;
1559 
1560             CGLTF_ASSERT_IF(sparse->indices_buffer_view->size < indices_req_size ||
1561                             sparse->values_buffer_view->size < values_req_size, cgltf_result_data_too_short);
1562 
1563             CGLTF_ASSERT_IF(sparse->indices_component_type != cgltf_component_type_r_8u &&
1564                             sparse->indices_component_type != cgltf_component_type_r_16u &&
1565                             sparse->indices_component_type != cgltf_component_type_r_32u, cgltf_result_invalid_gltf);
1566 
1567             if (sparse->indices_buffer_view->buffer->data)
1568             {
1569                 cgltf_size index_bound = cgltf_calc_index_bound(sparse->indices_buffer_view, sparse->indices_byte_offset, sparse->indices_component_type, sparse->count);
1570 
1571                 CGLTF_ASSERT_IF(index_bound >= accessor->count, cgltf_result_data_too_short);
1572             }
1573         }
1574     }
1575 
1576     for (cgltf_size i = 0; i < data->buffer_views_count; ++i)
1577     {
1578         cgltf_size req_size = data->buffer_views[i].offset + data->buffer_views[i].size;
1579 
1580         CGLTF_ASSERT_IF(data->buffer_views[i].buffer && data->buffer_views[i].buffer->size < req_size, cgltf_result_data_too_short);
1581 
1582         if (data->buffer_views[i].has_meshopt_compression)
1583         {
1584             cgltf_meshopt_compression* mc = &data->buffer_views[i].meshopt_compression;
1585 
1586             CGLTF_ASSERT_IF(mc->buffer == NULL || mc->buffer->size < mc->offset + mc->size, cgltf_result_data_too_short);
1587 
1588             CGLTF_ASSERT_IF(data->buffer_views[i].stride && mc->stride != data->buffer_views[i].stride, cgltf_result_invalid_gltf);
1589 
1590             CGLTF_ASSERT_IF(data->buffer_views[i].size != mc->stride * mc->count, cgltf_result_invalid_gltf);
1591 
1592             CGLTF_ASSERT_IF(mc->mode == cgltf_meshopt_compression_mode_invalid, cgltf_result_invalid_gltf);
1593 
1594             CGLTF_ASSERT_IF(mc->mode == cgltf_meshopt_compression_mode_attributes && !(mc->stride % 4 == 0 && mc->stride <= 256), cgltf_result_invalid_gltf);
1595 
1596             CGLTF_ASSERT_IF(mc->mode == cgltf_meshopt_compression_mode_triangles && mc->count % 3 != 0, cgltf_result_invalid_gltf);
1597 
1598             CGLTF_ASSERT_IF((mc->mode == cgltf_meshopt_compression_mode_triangles || mc->mode == cgltf_meshopt_compression_mode_indices) && mc->stride != 2 && mc->stride != 4, cgltf_result_invalid_gltf);
1599 
1600             CGLTF_ASSERT_IF((mc->mode == cgltf_meshopt_compression_mode_triangles || mc->mode == cgltf_meshopt_compression_mode_indices) && mc->filter != cgltf_meshopt_compression_filter_none, cgltf_result_invalid_gltf);
1601 
1602             CGLTF_ASSERT_IF(mc->filter == cgltf_meshopt_compression_filter_octahedral && mc->stride != 4 && mc->stride != 8, cgltf_result_invalid_gltf);
1603 
1604             CGLTF_ASSERT_IF(mc->filter == cgltf_meshopt_compression_filter_quaternion && mc->stride != 8, cgltf_result_invalid_gltf);
1605         }
1606     }
1607 
1608     for (cgltf_size i = 0; i < data->meshes_count; ++i)
1609     {
1610         if (data->meshes[i].weights)
1611         {
1612             CGLTF_ASSERT_IF(data->meshes[i].primitives_count && data->meshes[i].primitives[0].targets_count != data->meshes[i].weights_count, cgltf_result_invalid_gltf);
1613         }
1614 
1615         if (data->meshes[i].target_names)
1616         {
1617             CGLTF_ASSERT_IF(data->meshes[i].primitives_count && data->meshes[i].primitives[0].targets_count != data->meshes[i].target_names_count, cgltf_result_invalid_gltf);
1618         }
1619 
1620         for (cgltf_size j = 0; j < data->meshes[i].primitives_count; ++j)
1621         {
1622             CGLTF_ASSERT_IF(data->meshes[i].primitives[j].targets_count != data->meshes[i].primitives[0].targets_count, cgltf_result_invalid_gltf);
1623 
1624             if (data->meshes[i].primitives[j].attributes_count)
1625             {
1626                 cgltf_accessor* first = data->meshes[i].primitives[j].attributes[0].data;
1627 
1628                 for (cgltf_size k = 0; k < data->meshes[i].primitives[j].attributes_count; ++k)
1629                 {
1630                     CGLTF_ASSERT_IF(data->meshes[i].primitives[j].attributes[k].data->count != first->count, cgltf_result_invalid_gltf);
1631                 }
1632 
1633                 for (cgltf_size k = 0; k < data->meshes[i].primitives[j].targets_count; ++k)
1634                 {
1635                     for (cgltf_size m = 0; m < data->meshes[i].primitives[j].targets[k].attributes_count; ++m)
1636                     {
1637                         CGLTF_ASSERT_IF(data->meshes[i].primitives[j].targets[k].attributes[m].data->count != first->count, cgltf_result_invalid_gltf);
1638                     }
1639                 }
1640 
1641                 cgltf_accessor* indices = data->meshes[i].primitives[j].indices;
1642 
1643                 CGLTF_ASSERT_IF(indices &&
1644                     indices->component_type != cgltf_component_type_r_8u &&
1645                     indices->component_type != cgltf_component_type_r_16u &&
1646                     indices->component_type != cgltf_component_type_r_32u, cgltf_result_invalid_gltf);
1647 
1648                 if (indices && indices->buffer_view && indices->buffer_view->buffer->data)
1649                 {
1650                     cgltf_size index_bound = cgltf_calc_index_bound(indices->buffer_view, indices->offset, indices->component_type, indices->count);
1651 
1652                     CGLTF_ASSERT_IF(index_bound >= first->count, cgltf_result_data_too_short);
1653                 }
1654 
1655                 for (cgltf_size k = 0; k < data->meshes[i].primitives[j].mappings_count; ++k)
1656                 {
1657                     CGLTF_ASSERT_IF(data->meshes[i].primitives[j].mappings[k].variant >= data->variants_count, cgltf_result_invalid_gltf);
1658                 }
1659             }
1660         }
1661     }
1662 
1663     for (cgltf_size i = 0; i < data->nodes_count; ++i)
1664     {
1665         if (data->nodes[i].weights && data->nodes[i].mesh)
1666         {
1667             CGLTF_ASSERT_IF (data->nodes[i].mesh->primitives_count && data->nodes[i].mesh->primitives[0].targets_count != data->nodes[i].weights_count, cgltf_result_invalid_gltf);
1668         }
1669     }
1670 
1671     for (cgltf_size i = 0; i < data->nodes_count; ++i)
1672     {
1673         cgltf_node* p1 = data->nodes[i].parent;
1674         cgltf_node* p2 = p1 ? p1->parent : NULL;
1675 
1676         while (p1 && p2)
1677         {
1678             CGLTF_ASSERT_IF(p1 == p2, cgltf_result_invalid_gltf);
1679 
1680             p1 = p1->parent;
1681             p2 = p2->parent ? p2->parent->parent : NULL;
1682         }
1683     }
1684 
1685     for (cgltf_size i = 0; i < data->scenes_count; ++i)
1686     {
1687         for (cgltf_size j = 0; j < data->scenes[i].nodes_count; ++j)
1688         {
1689             CGLTF_ASSERT_IF(data->scenes[i].nodes[j]->parent, cgltf_result_invalid_gltf);
1690         }
1691     }
1692 
1693     for (cgltf_size i = 0; i < data->animations_count; ++i)
1694     {
1695         for (cgltf_size j = 0; j < data->animations[i].channels_count; ++j)
1696         {
1697             cgltf_animation_channel* channel = &data->animations[i].channels[j];
1698 
1699             if (!channel->target_node)
1700             {
1701                 continue;
1702             }
1703 
1704             cgltf_size components = 1;
1705 
1706             if (channel->target_path == cgltf_animation_path_type_weights)
1707             {
1708                 CGLTF_ASSERT_IF(!channel->target_node->mesh || !channel->target_node->mesh->primitives_count, cgltf_result_invalid_gltf);
1709 
1710                 components = channel->target_node->mesh->primitives[0].targets_count;
1711             }
1712 
1713             cgltf_size values = channel->sampler->interpolation == cgltf_interpolation_type_cubic_spline ? 3 : 1;
1714 
1715             CGLTF_ASSERT_IF(channel->sampler->input->count * components * values != channel->sampler->output->count, cgltf_result_data_too_short);
1716         }
1717     }
1718 
1719     return cgltf_result_success;
1720 }
1721 
1722 cgltf_result cgltf_copy_extras_json(const cgltf_data* data, const cgltf_extras* extras, char* dest, cgltf_size* dest_size)
1723 {
1724     cgltf_size json_size = extras->end_offset - extras->start_offset;
1725 
1726     if (!dest)
1727     {
1728         if (dest_size)
1729         {
1730             *dest_size = json_size + 1;
1731             return cgltf_result_success;
1732         }
1733         return cgltf_result_invalid_options;
1734     }
1735 
1736     if (*dest_size + 1 < json_size)
1737     {
1738         strncpy(dest, data->json + extras->start_offset, *dest_size - 1);
1739         dest[*dest_size - 1] = 0;
1740     }
1741     else
1742     {
1743         strncpy(dest, data->json + extras->start_offset, json_size);
1744         dest[json_size] = 0;
1745     }
1746 
1747     return cgltf_result_success;
1748 }
1749 
1750 static void cgltf_free_extras(cgltf_data* data, cgltf_extras* extras)
1751 {
1752     data->memory.free_func(data->memory.user_data, extras->data);
1753 }
1754 
1755 static void cgltf_free_extensions(cgltf_data* data, cgltf_extension* extensions, cgltf_size extensions_count)
1756 {
1757     for (cgltf_size i = 0; i < extensions_count; ++i)
1758     {
1759         data->memory.free_func(data->memory.user_data, extensions[i].name);
1760         data->memory.free_func(data->memory.user_data, extensions[i].data);
1761     }
1762     data->memory.free_func(data->memory.user_data, extensions);
1763 }
1764 
1765 static void cgltf_free_texture_view(cgltf_data* data, cgltf_texture_view* view)
1766 {
1767     cgltf_free_extensions(data, view->extensions, view->extensions_count);
1768     cgltf_free_extras(data, &view->extras);
1769 }
1770 
1771 void cgltf_free(cgltf_data* data)
1772 {
1773     if (!data)
1774     {
1775         return;
1776     }
1777 
1778     void (*file_release)(const struct cgltf_memory_options*, const struct cgltf_file_options*, void* data) = data->file.release ? data->file.release : cgltf_default_file_release;
1779 
1780     data->memory.free_func(data->memory.user_data, data->asset.copyright);
1781     data->memory.free_func(data->memory.user_data, data->asset.generator);
1782     data->memory.free_func(data->memory.user_data, data->asset.version);
1783     data->memory.free_func(data->memory.user_data, data->asset.min_version);
1784 
1785     cgltf_free_extensions(data, data->asset.extensions, data->asset.extensions_count);
1786     cgltf_free_extras(data, &data->asset.extras);
1787 
1788     for (cgltf_size i = 0; i < data->accessors_count; ++i)
1789     {
1790         data->memory.free_func(data->memory.user_data, data->accessors[i].name);
1791 
1792         if(data->accessors[i].is_sparse)
1793         {
1794             cgltf_free_extensions(data, data->accessors[i].sparse.extensions, data->accessors[i].sparse.extensions_count);
1795             cgltf_free_extensions(data, data->accessors[i].sparse.indices_extensions, data->accessors[i].sparse.indices_extensions_count);
1796             cgltf_free_extensions(data, data->accessors[i].sparse.values_extensions, data->accessors[i].sparse.values_extensions_count);
1797             cgltf_free_extras(data, &data->accessors[i].sparse.extras);
1798             cgltf_free_extras(data, &data->accessors[i].sparse.indices_extras);
1799             cgltf_free_extras(data, &data->accessors[i].sparse.values_extras);
1800         }
1801         cgltf_free_extensions(data, data->accessors[i].extensions, data->accessors[i].extensions_count);
1802         cgltf_free_extras(data, &data->accessors[i].extras);
1803     }
1804     data->memory.free_func(data->memory.user_data, data->accessors);
1805 
1806     for (cgltf_size i = 0; i < data->buffer_views_count; ++i)
1807     {
1808         data->memory.free_func(data->memory.user_data, data->buffer_views[i].name);
1809         data->memory.free_func(data->memory.user_data, data->buffer_views[i].data);
1810 
1811         cgltf_free_extensions(data, data->buffer_views[i].extensions, data->buffer_views[i].extensions_count);
1812         cgltf_free_extras(data, &data->buffer_views[i].extras);
1813     }
1814     data->memory.free_func(data->memory.user_data, data->buffer_views);
1815 
1816     for (cgltf_size i = 0; i < data->buffers_count; ++i)
1817     {
1818         data->memory.free_func(data->memory.user_data, data->buffers[i].name);
1819 
1820         if (data->buffers[i].data_free_method == cgltf_data_free_method_file_release)
1821         {
1822             file_release(&data->memory, &data->file, data->buffers[i].data);
1823         }
1824         else if (data->buffers[i].data_free_method == cgltf_data_free_method_memory_free)
1825         {
1826             data->memory.free_func(data->memory.user_data, data->buffers[i].data);
1827         }
1828 
1829         data->memory.free_func(data->memory.user_data, data->buffers[i].uri);
1830 
1831         cgltf_free_extensions(data, data->buffers[i].extensions, data->buffers[i].extensions_count);
1832         cgltf_free_extras(data, &data->buffers[i].extras);
1833     }
1834     data->memory.free_func(data->memory.user_data, data->buffers);
1835 
1836     for (cgltf_size i = 0; i < data->meshes_count; ++i)
1837     {
1838         data->memory.free_func(data->memory.user_data, data->meshes[i].name);
1839 
1840         for (cgltf_size j = 0; j < data->meshes[i].primitives_count; ++j)
1841         {
1842             for (cgltf_size k = 0; k < data->meshes[i].primitives[j].attributes_count; ++k)
1843             {
1844                 data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].attributes[k].name);
1845             }
1846 
1847             data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].attributes);
1848 
1849             for (cgltf_size k = 0; k < data->meshes[i].primitives[j].targets_count; ++k)
1850             {
1851                 for (cgltf_size m = 0; m < data->meshes[i].primitives[j].targets[k].attributes_count; ++m)
1852                 {
1853                     data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].targets[k].attributes[m].name);
1854                 }
1855 
1856                 data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].targets[k].attributes);
1857             }
1858 
1859             data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].targets);
1860 
1861             if (data->meshes[i].primitives[j].has_draco_mesh_compression)
1862             {
1863                 for (cgltf_size k = 0; k < data->meshes[i].primitives[j].draco_mesh_compression.attributes_count; ++k)
1864                 {
1865                     data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].draco_mesh_compression.attributes[k].name);
1866                 }
1867 
1868                 data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].draco_mesh_compression.attributes);
1869             }
1870 
1871             for (cgltf_size k = 0; k < data->meshes[i].primitives[j].mappings_count; ++k)
1872             {
1873                 cgltf_free_extras(data, &data->meshes[i].primitives[j].mappings[k].extras);
1874             }
1875 
1876             data->memory.free_func(data->memory.user_data, data->meshes[i].primitives[j].mappings);
1877 
1878             cgltf_free_extensions(data, data->meshes[i].primitives[j].extensions, data->meshes[i].primitives[j].extensions_count);
1879             cgltf_free_extras(data, &data->meshes[i].primitives[j].extras);
1880         }
1881 
1882         data->memory.free_func(data->memory.user_data, data->meshes[i].primitives);
1883         data->memory.free_func(data->memory.user_data, data->meshes[i].weights);
1884 
1885         for (cgltf_size j = 0; j < data->meshes[i].target_names_count; ++j)
1886         {
1887             data->memory.free_func(data->memory.user_data, data->meshes[i].target_names[j]);
1888         }
1889 
1890         cgltf_free_extensions(data, data->meshes[i].extensions, data->meshes[i].extensions_count);
1891         cgltf_free_extras(data, &data->meshes[i].extras);
1892 
1893         data->memory.free_func(data->memory.user_data, data->meshes[i].target_names);
1894     }
1895 
1896     data->memory.free_func(data->memory.user_data, data->meshes);
1897 
1898     for (cgltf_size i = 0; i < data->materials_count; ++i)
1899     {
1900         data->memory.free_func(data->memory.user_data, data->materials[i].name);
1901 
1902         if(data->materials[i].has_pbr_metallic_roughness)
1903         {
1904             cgltf_free_texture_view(data, &data->materials[i].pbr_metallic_roughness.metallic_roughness_texture);
1905             cgltf_free_texture_view(data, &data->materials[i].pbr_metallic_roughness.base_color_texture);
1906         }
1907         if(data->materials[i].has_pbr_specular_glossiness)
1908         {
1909             cgltf_free_texture_view(data, &data->materials[i].pbr_specular_glossiness.diffuse_texture);
1910             cgltf_free_texture_view(data, &data->materials[i].pbr_specular_glossiness.specular_glossiness_texture);
1911         }
1912         if(data->materials[i].has_clearcoat)
1913         {
1914             cgltf_free_texture_view(data, &data->materials[i].clearcoat.clearcoat_texture);
1915             cgltf_free_texture_view(data, &data->materials[i].clearcoat.clearcoat_roughness_texture);
1916             cgltf_free_texture_view(data, &data->materials[i].clearcoat.clearcoat_normal_texture);
1917         }
1918         if(data->materials[i].has_specular)
1919         {
1920             cgltf_free_texture_view(data, &data->materials[i].specular.specular_texture);
1921             cgltf_free_texture_view(data, &data->materials[i].specular.specular_color_texture);
1922         }
1923         if(data->materials[i].has_transmission)
1924         {
1925             cgltf_free_texture_view(data, &data->materials[i].transmission.transmission_texture);
1926         }
1927         if (data->materials[i].has_volume)
1928         {
1929             cgltf_free_texture_view(data, &data->materials[i].volume.thickness_texture);
1930         }
1931         if(data->materials[i].has_sheen)
1932         {
1933             cgltf_free_texture_view(data, &data->materials[i].sheen.sheen_color_texture);
1934             cgltf_free_texture_view(data, &data->materials[i].sheen.sheen_roughness_texture);
1935         }
1936         if(data->materials[i].has_iridescence)
1937         {
1938             cgltf_free_texture_view(data, &data->materials[i].iridescence.iridescence_texture);
1939             cgltf_free_texture_view(data, &data->materials[i].iridescence.iridescence_thickness_texture);
1940         }
1941 
1942         cgltf_free_texture_view(data, &data->materials[i].normal_texture);
1943         cgltf_free_texture_view(data, &data->materials[i].occlusion_texture);
1944         cgltf_free_texture_view(data, &data->materials[i].emissive_texture);
1945 
1946         cgltf_free_extensions(data, data->materials[i].extensions, data->materials[i].extensions_count);
1947         cgltf_free_extras(data, &data->materials[i].extras);
1948     }
1949 
1950     data->memory.free_func(data->memory.user_data, data->materials);
1951 
1952     for (cgltf_size i = 0; i < data->images_count; ++i) 
1953     {
1954         data->memory.free_func(data->memory.user_data, data->images[i].name);
1955         data->memory.free_func(data->memory.user_data, data->images[i].uri);
1956         data->memory.free_func(data->memory.user_data, data->images[i].mime_type);
1957 
1958         cgltf_free_extensions(data, data->images[i].extensions, data->images[i].extensions_count);
1959         cgltf_free_extras(data, &data->images[i].extras);
1960     }
1961 
1962     data->memory.free_func(data->memory.user_data, data->images);
1963 
1964     for (cgltf_size i = 0; i < data->textures_count; ++i)
1965     {
1966         data->memory.free_func(data->memory.user_data, data->textures[i].name);
1967 
1968         cgltf_free_extensions(data, data->textures[i].extensions, data->textures[i].extensions_count);
1969         cgltf_free_extras(data, &data->textures[i].extras);
1970     }
1971 
1972     data->memory.free_func(data->memory.user_data, data->textures);
1973 
1974     for (cgltf_size i = 0; i < data->samplers_count; ++i)
1975     {
1976         data->memory.free_func(data->memory.user_data, data->samplers[i].name);
1977 
1978         cgltf_free_extensions(data, data->samplers[i].extensions, data->samplers[i].extensions_count);
1979         cgltf_free_extras(data, &data->samplers[i].extras);
1980     }
1981 
1982     data->memory.free_func(data->memory.user_data, data->samplers);
1983 
1984     for (cgltf_size i = 0; i < data->skins_count; ++i)
1985     {
1986         data->memory.free_func(data->memory.user_data, data->skins[i].name);
1987         data->memory.free_func(data->memory.user_data, data->skins[i].joints);
1988 
1989         cgltf_free_extensions(data, data->skins[i].extensions, data->skins[i].extensions_count);
1990         cgltf_free_extras(data, &data->skins[i].extras);
1991     }
1992 
1993     data->memory.free_func(data->memory.user_data, data->skins);
1994 
1995     for (cgltf_size i = 0; i < data->cameras_count; ++i)
1996     {
1997         data->memory.free_func(data->memory.user_data, data->cameras[i].name);
1998 
1999         if (data->cameras[i].type == cgltf_camera_type_perspective)
2000         {
2001             cgltf_free_extras(data, &data->cameras[i].data.perspective.extras);
2002         }
2003         else if (data->cameras[i].type == cgltf_camera_type_orthographic)
2004         {
2005             cgltf_free_extras(data, &data->cameras[i].data.orthographic.extras);
2006         }
2007 
2008         cgltf_free_extensions(data, data->cameras[i].extensions, data->cameras[i].extensions_count);
2009         cgltf_free_extras(data, &data->cameras[i].extras);
2010     }
2011 
2012     data->memory.free_func(data->memory.user_data, data->cameras);
2013 
2014     for (cgltf_size i = 0; i < data->lights_count; ++i)
2015     {
2016         data->memory.free_func(data->memory.user_data, data->lights[i].name);
2017 
2018         cgltf_free_extras(data, &data->lights[i].extras);
2019     }
2020 
2021     data->memory.free_func(data->memory.user_data, data->lights);
2022 
2023     for (cgltf_size i = 0; i < data->nodes_count; ++i)
2024     {
2025         data->memory.free_func(data->memory.user_data, data->nodes[i].name);
2026         data->memory.free_func(data->memory.user_data, data->nodes[i].children);
2027         data->memory.free_func(data->memory.user_data, data->nodes[i].weights);
2028 
2029         if (data->nodes[i].has_mesh_gpu_instancing)
2030         {
2031             for (cgltf_size j = 0; j < data->nodes[i].mesh_gpu_instancing.attributes_count; ++j)
2032             {
2033                 data->memory.free_func(data->memory.user_data, data->nodes[i].mesh_gpu_instancing.attributes[j].name);
2034             }
2035 
2036             data->memory.free_func(data->memory.user_data, data->nodes[i].mesh_gpu_instancing.attributes);
2037         }
2038 
2039         cgltf_free_extensions(data, data->nodes[i].extensions, data->nodes[i].extensions_count);
2040         cgltf_free_extras(data, &data->nodes[i].extras);
2041     }
2042 
2043     data->memory.free_func(data->memory.user_data, data->nodes);
2044 
2045     for (cgltf_size i = 0; i < data->scenes_count; ++i)
2046     {
2047         data->memory.free_func(data->memory.user_data, data->scenes[i].name);
2048         data->memory.free_func(data->memory.user_data, data->scenes[i].nodes);
2049 
2050         cgltf_free_extensions(data, data->scenes[i].extensions, data->scenes[i].extensions_count);
2051         cgltf_free_extras(data, &data->scenes[i].extras);
2052     }
2053 
2054     data->memory.free_func(data->memory.user_data, data->scenes);
2055 
2056     for (cgltf_size i = 0; i < data->animations_count; ++i)
2057     {
2058         data->memory.free_func(data->memory.user_data, data->animations[i].name);
2059         for (cgltf_size j = 0; j <  data->animations[i].samplers_count; ++j)
2060         {
2061             cgltf_free_extensions(data, data->animations[i].samplers[j].extensions, data->animations[i].samplers[j].extensions_count);
2062             cgltf_free_extras(data, &data->animations[i].samplers[j].extras);
2063         }
2064         data->memory.free_func(data->memory.user_data, data->animations[i].samplers);
2065 
2066         for (cgltf_size j = 0; j <  data->animations[i].channels_count; ++j)
2067         {
2068             cgltf_free_extensions(data, data->animations[i].channels[j].extensions, data->animations[i].channels[j].extensions_count);
2069             cgltf_free_extras(data, &data->animations[i].channels[j].extras);
2070         }
2071         data->memory.free_func(data->memory.user_data, data->animations[i].channels);
2072 
2073         cgltf_free_extensions(data, data->animations[i].extensions, data->animations[i].extensions_count);
2074         cgltf_free_extras(data, &data->animations[i].extras);
2075     }
2076 
2077     data->memory.free_func(data->memory.user_data, data->animations);
2078 
2079     for (cgltf_size i = 0; i < data->variants_count; ++i)
2080     {
2081         data->memory.free_func(data->memory.user_data, data->variants[i].name);
2082 
2083         cgltf_free_extras(data, &data->variants[i].extras);
2084     }
2085 
2086     data->memory.free_func(data->memory.user_data, data->variants);
2087 
2088     cgltf_free_extensions(data, data->data_extensions, data->data_extensions_count);
2089     cgltf_free_extras(data, &data->extras);
2090 
2091     for (cgltf_size i = 0; i < data->extensions_used_count; ++i)
2092     {
2093         data->memory.free_func(data->memory.user_data, data->extensions_used[i]);
2094     }
2095 
2096     data->memory.free_func(data->memory.user_data, data->extensions_used);
2097 
2098     for (cgltf_size i = 0; i < data->extensions_required_count; ++i)
2099     {
2100         data->memory.free_func(data->memory.user_data, data->extensions_required[i]);
2101     }
2102 
2103     data->memory.free_func(data->memory.user_data, data->extensions_required);
2104 
2105     file_release(&data->memory, &data->file, data->file_data);
2106 
2107     data->memory.free_func(data->memory.user_data, data);
2108 }
2109 
2110 void cgltf_node_transform_local(const cgltf_node* node, cgltf_float* out_matrix)
2111 {
2112     cgltf_float* lm = out_matrix;
2113 
2114     if (node->has_matrix)
2115     {
2116         memcpy(lm, node->matrix, sizeof(float) * 16);
2117     }
2118     else
2119     {
2120         float tx = node->translation[0];
2121         float ty = node->translation[1];
2122         float tz = node->translation[2];
2123 
2124         float qx = node->rotation[0];
2125         float qy = node->rotation[1];
2126         float qz = node->rotation[2];
2127         float qw = node->rotation[3];
2128 
2129         float sx = node->scale[0];
2130         float sy = node->scale[1];
2131         float sz = node->scale[2];
2132 
2133         lm[0] = (1 - 2 * qy*qy - 2 * qz*qz) * sx;
2134         lm[1] = (2 * qx*qy + 2 * qz*qw) * sx;
2135         lm[2] = (2 * qx*qz - 2 * qy*qw) * sx;
2136         lm[3] = 0.f;
2137 
2138         lm[4] = (2 * qx*qy - 2 * qz*qw) * sy;
2139         lm[5] = (1 - 2 * qx*qx - 2 * qz*qz) * sy;
2140         lm[6] = (2 * qy*qz + 2 * qx*qw) * sy;
2141         lm[7] = 0.f;
2142 
2143         lm[8] = (2 * qx*qz + 2 * qy*qw) * sz;
2144         lm[9] = (2 * qy*qz - 2 * qx*qw) * sz;
2145         lm[10] = (1 - 2 * qx*qx - 2 * qy*qy) * sz;
2146         lm[11] = 0.f;
2147 
2148         lm[12] = tx;
2149         lm[13] = ty;
2150         lm[14] = tz;
2151         lm[15] = 1.f;
2152     }
2153 }
2154 
2155 void cgltf_node_transform_world(const cgltf_node* node, cgltf_float* out_matrix)
2156 {
2157     cgltf_float* lm = out_matrix;
2158     cgltf_node_transform_local(node, lm);
2159 
2160     const cgltf_node* parent = node->parent;
2161 
2162     while (parent)
2163     {
2164         float pm[16];
2165         cgltf_node_transform_local(parent, pm);
2166 
2167         for (int i = 0; i < 4; ++i)
2168         {
2169             float l0 = lm[i * 4 + 0];
2170             float l1 = lm[i * 4 + 1];
2171             float l2 = lm[i * 4 + 2];
2172 
2173             float r0 = l0 * pm[0] + l1 * pm[4] + l2 * pm[8];
2174             float r1 = l0 * pm[1] + l1 * pm[5] + l2 * pm[9];
2175             float r2 = l0 * pm[2] + l1 * pm[6] + l2 * pm[10];
2176 
2177             lm[i * 4 + 0] = r0;
2178             lm[i * 4 + 1] = r1;
2179             lm[i * 4 + 2] = r2;
2180         }
2181 
2182         lm[12] += pm[12];
2183         lm[13] += pm[13];
2184         lm[14] += pm[14];
2185 
2186         parent = parent->parent;
2187     }
2188 }
2189 
2190 static cgltf_ssize cgltf_component_read_integer(const void* in, cgltf_component_type component_type)
2191 {
2192     switch (component_type)
2193     {
2194         case cgltf_component_type_r_16:
2195             return *((const int16_t*) in);
2196         case cgltf_component_type_r_16u:
2197             return *((const uint16_t*) in);
2198         case cgltf_component_type_r_32u:
2199             return *((const uint32_t*) in);
2200         case cgltf_component_type_r_32f:
2201             return (cgltf_ssize)*((const float*) in);
2202         case cgltf_component_type_r_8:
2203             return *((const int8_t*) in);
2204         case cgltf_component_type_r_8u:
2205             return *((const uint8_t*) in);
2206         default:
2207             return 0;
2208     }
2209 }
2210 
2211 static cgltf_size cgltf_component_read_index(const void* in, cgltf_component_type component_type)
2212 {
2213     switch (component_type)
2214     {
2215         case cgltf_component_type_r_16u:
2216             return *((const uint16_t*) in);
2217         case cgltf_component_type_r_32u:
2218             return *((const uint32_t*) in);
2219         case cgltf_component_type_r_32f:
2220             return (cgltf_size)*((const float*) in);
2221         case cgltf_component_type_r_8u:
2222             return *((const uint8_t*) in);
2223         default:
2224             return 0;
2225     }
2226 }
2227 
2228 static cgltf_float cgltf_component_read_float(const void* in, cgltf_component_type component_type, cgltf_bool normalized)
2229 {
2230     if (component_type == cgltf_component_type_r_32f)
2231     {
2232         return *((const float*) in);
2233     }
2234 
2235     if (normalized)
2236     {
2237         switch (component_type)
2238         {
2239             // note: glTF spec doesn't currently define normalized conversions for 32-bit integers
2240             case cgltf_component_type_r_16:
2241                 return *((const int16_t*) in) / (cgltf_float)32767;
2242             case cgltf_component_type_r_16u:
2243                 return *((const uint16_t*) in) / (cgltf_float)65535;
2244             case cgltf_component_type_r_8:
2245                 return *((const int8_t*) in) / (cgltf_float)127;
2246             case cgltf_component_type_r_8u:
2247                 return *((const uint8_t*) in) / (cgltf_float)255;
2248             default:
2249                 return 0;
2250         }
2251     }
2252 
2253     return (cgltf_float)cgltf_component_read_integer(in, component_type);
2254 }
2255 
2256 static cgltf_size cgltf_component_size(cgltf_component_type component_type);
2257 
2258 static cgltf_bool cgltf_element_read_float(const uint8_t* element, cgltf_type type, cgltf_component_type component_type, cgltf_bool normalized, cgltf_float* out, cgltf_size element_size)
2259 {
2260     cgltf_size num_components = cgltf_num_components(type);
2261 
2262     if (element_size < num_components) {
2263         return 0;
2264     }
2265 
2266     // There are three special cases for component extraction, see #data-alignment in the 2.0 spec.
2267 
2268     cgltf_size component_size = cgltf_component_size(component_type);
2269 
2270     if (type == cgltf_type_mat2 && component_size == 1)
2271     {
2272         out[0] = cgltf_component_read_float(element, component_type, normalized);
2273         out[1] = cgltf_component_read_float(element + 1, component_type, normalized);
2274         out[2] = cgltf_component_read_float(element + 4, component_type, normalized);
2275         out[3] = cgltf_component_read_float(element + 5, component_type, normalized);
2276         return 1;
2277     }
2278 
2279     if (type == cgltf_type_mat3 && component_size == 1)
2280     {
2281         out[0] = cgltf_component_read_float(element, component_type, normalized);
2282         out[1] = cgltf_component_read_float(element + 1, component_type, normalized);
2283         out[2] = cgltf_component_read_float(element + 2, component_type, normalized);
2284         out[3] = cgltf_component_read_float(element + 4, component_type, normalized);
2285         out[4] = cgltf_component_read_float(element + 5, component_type, normalized);
2286         out[5] = cgltf_component_read_float(element + 6, component_type, normalized);
2287         out[6] = cgltf_component_read_float(element + 8, component_type, normalized);
2288         out[7] = cgltf_component_read_float(element + 9, component_type, normalized);
2289         out[8] = cgltf_component_read_float(element + 10, component_type, normalized);
2290         return 1;
2291     }
2292 
2293     if (type == cgltf_type_mat3 && component_size == 2)
2294     {
2295         out[0] = cgltf_component_read_float(element, component_type, normalized);
2296         out[1] = cgltf_component_read_float(element + 2, component_type, normalized);
2297         out[2] = cgltf_component_read_float(element + 4, component_type, normalized);
2298         out[3] = cgltf_component_read_float(element + 8, component_type, normalized);
2299         out[4] = cgltf_component_read_float(element + 10, component_type, normalized);
2300         out[5] = cgltf_component_read_float(element + 12, component_type, normalized);
2301         out[6] = cgltf_component_read_float(element + 16, component_type, normalized);
2302         out[7] = cgltf_component_read_float(element + 18, component_type, normalized);
2303         out[8] = cgltf_component_read_float(element + 20, component_type, normalized);
2304         return 1;
2305     }
2306 
2307     for (cgltf_size i = 0; i < num_components; ++i)
2308     {
2309         out[i] = cgltf_component_read_float(element + component_size * i, component_type, normalized);
2310     }
2311     return 1;
2312 }
2313 
2314 const uint8_t* cgltf_buffer_view_data(const cgltf_buffer_view* view)
2315 {
2316     if (view->data)
2317         return (const uint8_t*)view->data;
2318 
2319     if (!view->buffer->data)
2320         return NULL;
2321 
2322     const uint8_t* result = (const uint8_t*)view->buffer->data;
2323     result += view->offset;
2324     return result;
2325 }
2326 
2327 cgltf_bool cgltf_accessor_read_float(const cgltf_accessor* accessor, cgltf_size index, cgltf_float* out, cgltf_size element_size)
2328 {
2329     if (accessor->is_sparse)
2330     {
2331         return 0;
2332     }
2333     if (accessor->buffer_view == NULL)
2334     {
2335         memset(out, 0, element_size * sizeof(cgltf_float));
2336         return 1;
2337     }
2338     const uint8_t* element = cgltf_buffer_view_data(accessor->buffer_view);
2339     if (element == NULL)
2340     {
2341         return 0;
2342     }
2343     element += accessor->offset + accessor->stride * index;
2344     return cgltf_element_read_float(element, accessor->type, accessor->component_type, accessor->normalized, out, element_size);
2345 }
2346 
2347 cgltf_size cgltf_accessor_unpack_floats(const cgltf_accessor* accessor, cgltf_float* out, cgltf_size float_count)
2348 {
2349     cgltf_size floats_per_element = cgltf_num_components(accessor->type);
2350     cgltf_size available_floats = accessor->count * floats_per_element;
2351     if (out == NULL)
2352     {
2353         return available_floats;
2354     }
2355 
2356     float_count = available_floats < float_count ? available_floats : float_count;
2357     cgltf_size element_count = float_count / floats_per_element;
2358 
2359     // First pass: convert each element in the base accessor.
2360     cgltf_float* dest = out;
2361     cgltf_accessor dense = *accessor;
2362     dense.is_sparse = 0;
2363     for (cgltf_size index = 0; index < element_count; index++, dest += floats_per_element)
2364     {
2365         if (!cgltf_accessor_read_float(&dense, index, dest, floats_per_element))
2366         {
2367             return 0;
2368         }
2369     }
2370 
2371     // Second pass: write out each element in the sparse accessor.
2372     if (accessor->is_sparse)
2373     {
2374         const cgltf_accessor_sparse* sparse = &dense.sparse;
2375 
2376         const uint8_t* index_data = cgltf_buffer_view_data(sparse->indices_buffer_view);
2377         const uint8_t* reader_head = cgltf_buffer_view_data(sparse->values_buffer_view);
2378 
2379         if (index_data == NULL || reader_head == NULL)
2380         {
2381             return 0;
2382         }
2383 
2384         index_data += sparse->indices_byte_offset;
2385         reader_head += sparse->values_byte_offset;
2386 
2387         cgltf_size index_stride = cgltf_component_size(sparse->indices_component_type);
2388         for (cgltf_size reader_index = 0; reader_index < sparse->count; reader_index++, index_data += index_stride)
2389         {
2390             size_t writer_index = cgltf_component_read_index(index_data, sparse->indices_component_type);
2391             float* writer_head = out + writer_index * floats_per_element;
2392 
2393             if (!cgltf_element_read_float(reader_head, dense.type, dense.component_type, dense.normalized, writer_head, floats_per_element))
2394             {
2395                 return 0;
2396             }
2397 
2398             reader_head += dense.stride;
2399         }
2400     }
2401 
2402     return element_count * floats_per_element;
2403 }
2404 
2405 static cgltf_uint cgltf_component_read_uint(const void* in, cgltf_component_type component_type)
2406 {
2407     switch (component_type)
2408     {
2409         case cgltf_component_type_r_8:
2410             return *((const int8_t*) in);
2411 
2412         case cgltf_component_type_r_8u:
2413             return *((const uint8_t*) in);
2414 
2415         case cgltf_component_type_r_16:
2416             return *((const int16_t*) in);
2417 
2418         case cgltf_component_type_r_16u:
2419             return *((const uint16_t*) in);
2420 
2421         case cgltf_component_type_r_32u:
2422             return *((const uint32_t*) in);
2423 
2424         default:
2425             return 0;
2426     }
2427 }
2428 
2429 static cgltf_bool cgltf_element_read_uint(const uint8_t* element, cgltf_type type, cgltf_component_type component_type, cgltf_uint* out, cgltf_size element_size)
2430 {
2431     cgltf_size num_components = cgltf_num_components(type);
2432 
2433     if (element_size < num_components)
2434     {
2435         return 0;
2436     }
2437 
2438     // Reading integer matrices is not a valid use case
2439     if (type == cgltf_type_mat2 || type == cgltf_type_mat3 || type == cgltf_type_mat4)
2440     {
2441         return 0;
2442     }
2443 
2444     cgltf_size component_size = cgltf_component_size(component_type);
2445 
2446     for (cgltf_size i = 0; i < num_components; ++i)
2447     {
2448         out[i] = cgltf_component_read_uint(element + component_size * i, component_type);
2449     }
2450     return 1;
2451 }
2452 
2453 cgltf_bool cgltf_accessor_read_uint(const cgltf_accessor* accessor, cgltf_size index, cgltf_uint* out, cgltf_size element_size)
2454 {
2455     if (accessor->is_sparse)
2456     {
2457         return 0;
2458     }
2459     if (accessor->buffer_view == NULL)
2460     {
2461         memset(out, 0, element_size * sizeof( cgltf_uint ));
2462         return 1;
2463     }
2464     const uint8_t* element = cgltf_buffer_view_data(accessor->buffer_view);
2465     if (element == NULL)
2466     {
2467         return 0;
2468     }
2469     element += accessor->offset + accessor->stride * index;
2470     return cgltf_element_read_uint(element, accessor->type, accessor->component_type, out, element_size);
2471 }
2472 
2473 cgltf_size cgltf_accessor_read_index(const cgltf_accessor* accessor, cgltf_size index)
2474 {
2475     if (accessor->is_sparse)
2476     {
2477         return 0; // This is an error case, but we can't communicate the error with existing interface.
2478     }
2479     if (accessor->buffer_view == NULL)
2480     {
2481         return 0;
2482     }
2483     const uint8_t* element = cgltf_buffer_view_data(accessor->buffer_view);
2484     if (element == NULL)
2485     {
2486         return 0; // This is an error case, but we can't communicate the error with existing interface.
2487     }
2488     element += accessor->offset + accessor->stride * index;
2489     return cgltf_component_read_index(element, accessor->component_type);
2490 }
2491 
2492 #define CGLTF_ERROR_JSON -1
2493 #define CGLTF_ERROR_NOMEM -2
2494 #define CGLTF_ERROR_LEGACY -3
2495 
2496 #define CGLTF_CHECK_TOKTYPE(tok_, type_) if ((tok_).type != (type_)) { return CGLTF_ERROR_JSON; }
2497 #define CGLTF_CHECK_TOKTYPE_RETTYPE(tok_, type_, ret_) if ((tok_).type != (type_)) { return (ret_)CGLTF_ERROR_JSON; }
2498 #define CGLTF_CHECK_KEY(tok_) if ((tok_).type != JSMN_STRING || (tok_).size == 0) { return CGLTF_ERROR_JSON; } /* checking size for 0 verifies that a value follows the key */
2499 
2500 #define CGLTF_PTRINDEX(type, idx) (type*)((cgltf_size)idx + 1)
2501 #define CGLTF_PTRFIXUP(var, data, size) if (var) { if ((cgltf_size)var > size) { return CGLTF_ERROR_JSON; } var = &data[(cgltf_size)var-1]; }
2502 #define CGLTF_PTRFIXUP_REQ(var, data, size) if (!var || (cgltf_size)var > size) { return CGLTF_ERROR_JSON; } var = &data[(cgltf_size)var-1];
2503 
2504 static int cgltf_json_strcmp(jsmntok_t const* tok, const uint8_t* json_chunk, const char* str)
2505 {
2506     CGLTF_CHECK_TOKTYPE(*tok, JSMN_STRING);
2507     size_t const str_len = strlen(str);
2508     size_t const name_length = (size_t)(tok->end - tok->start);
2509     return (str_len == name_length) ? strncmp((const char*)json_chunk + tok->start, str, str_len) : 128;
2510 }
2511 
2512 static int cgltf_json_to_int(jsmntok_t const* tok, const uint8_t* json_chunk)
2513 {
2514     CGLTF_CHECK_TOKTYPE(*tok, JSMN_PRIMITIVE);
2515     char tmp[128];
2516     int size = (size_t)(tok->end - tok->start) < sizeof(tmp) ? (int)(tok->end - tok->start) : (int)(sizeof(tmp) - 1);
2517     strncpy(tmp, (const char*)json_chunk + tok->start, size);
2518     tmp[size] = 0;
2519     return CGLTF_ATOI(tmp);
2520 }
2521 
2522 static cgltf_size cgltf_json_to_size(jsmntok_t const* tok, const uint8_t* json_chunk)
2523 {
2524     CGLTF_CHECK_TOKTYPE_RETTYPE(*tok, JSMN_PRIMITIVE, cgltf_size);
2525     char tmp[128];
2526     int size = (size_t)(tok->end - tok->start) < sizeof(tmp) ? (int)(tok->end - tok->start) : (int)(sizeof(tmp) - 1);
2527     strncpy(tmp, (const char*)json_chunk + tok->start, size);
2528     tmp[size] = 0;
2529     return (cgltf_size)CGLTF_ATOLL(tmp);
2530 }
2531 
2532 static cgltf_float cgltf_json_to_float(jsmntok_t const* tok, const uint8_t* json_chunk)
2533 {
2534     CGLTF_CHECK_TOKTYPE(*tok, JSMN_PRIMITIVE);
2535     char tmp[128];
2536     int size = (size_t)(tok->end - tok->start) < sizeof(tmp) ? (int)(tok->end - tok->start) : (int)(sizeof(tmp) - 1);
2537     strncpy(tmp, (const char*)json_chunk + tok->start, size);
2538     tmp[size] = 0;
2539     return (cgltf_float)CGLTF_ATOF(tmp);
2540 }
2541 
2542 static cgltf_bool cgltf_json_to_bool(jsmntok_t const* tok, const uint8_t* json_chunk)
2543 {
2544     int size = (int)(tok->end - tok->start);
2545     return size == 4 && memcmp(json_chunk + tok->start, "true", 4) == 0;
2546 }
2547 
2548 static int cgltf_skip_json(jsmntok_t const* tokens, int i)
2549 {
2550     int end = i + 1;
2551 
2552     while (i < end)
2553     {
2554         switch (tokens[i].type)
2555         {
2556         case JSMN_OBJECT:
2557             end += tokens[i].size * 2;
2558             break;
2559 
2560         case JSMN_ARRAY:
2561             end += tokens[i].size;
2562             break;
2563 
2564         case JSMN_PRIMITIVE:
2565         case JSMN_STRING:
2566             break;
2567 
2568         default:
2569             return -1;
2570         }
2571 
2572         i++;
2573     }
2574 
2575     return i;
2576 }
2577 
2578 static void cgltf_fill_float_array(float* out_array, int size, float value)
2579 {
2580     for (int j = 0; j < size; ++j)
2581     {
2582         out_array[j] = value;
2583     }
2584 }
2585 
2586 static int cgltf_parse_json_float_array(jsmntok_t const* tokens, int i, const uint8_t* json_chunk, float* out_array, int size)
2587 {
2588     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_ARRAY);
2589     if (tokens[i].size != size)
2590     {
2591         return CGLTF_ERROR_JSON;
2592     }
2593     ++i;
2594     for (int j = 0; j < size; ++j)
2595     {
2596         CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_PRIMITIVE);
2597         out_array[j] = cgltf_json_to_float(tokens + i, json_chunk);
2598         ++i;
2599     }
2600     return i;
2601 }
2602 
2603 static int cgltf_parse_json_string(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, char** out_string)
2604 {
2605     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_STRING);
2606     if (*out_string)
2607     {
2608         return CGLTF_ERROR_JSON;
2609     }
2610     int size = (int)(tokens[i].end - tokens[i].start);
2611     char* result = (char*)options->memory.alloc_func(options->memory.user_data, size + 1);
2612     if (!result)
2613     {
2614         return CGLTF_ERROR_NOMEM;
2615     }
2616     strncpy(result, (const char*)json_chunk + tokens[i].start, size);
2617     result[size] = 0;
2618     *out_string = result;
2619     return i + 1;
2620 }
2621 
2622 static int cgltf_parse_json_array(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, size_t element_size, void** out_array, cgltf_size* out_size)
2623 {
2624     (void)json_chunk;
2625     if (tokens[i].type != JSMN_ARRAY)
2626     {
2627         return tokens[i].type == JSMN_OBJECT ? CGLTF_ERROR_LEGACY : CGLTF_ERROR_JSON;
2628     }
2629     if (*out_array)
2630     {
2631         return CGLTF_ERROR_JSON;
2632     }
2633     int size = tokens[i].size;
2634     void* result = cgltf_calloc(options, element_size, size);
2635     if (!result)
2636     {
2637         return CGLTF_ERROR_NOMEM;
2638     }
2639     *out_array = result;
2640     *out_size = size;
2641     return i + 1;
2642 }
2643 
2644 static int cgltf_parse_json_string_array(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, char*** out_array, cgltf_size* out_size)
2645 {
2646     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_ARRAY);
2647     i = cgltf_parse_json_array(options, tokens, i, json_chunk, sizeof(char*), (void**)out_array, out_size);
2648     if (i < 0)
2649     {
2650         return i;
2651     }
2652 
2653     for (cgltf_size j = 0; j < *out_size; ++j)
2654     {
2655         i = cgltf_parse_json_string(options, tokens, i, json_chunk, j + (*out_array));
2656         if (i < 0)
2657         {
2658             return i;
2659         }
2660     }
2661     return i;
2662 }
2663 
2664 static void cgltf_parse_attribute_type(const char* name, cgltf_attribute_type* out_type, int* out_index)
2665 {
2666     if (*name == '_')
2667     {
2668         *out_type = cgltf_attribute_type_custom;
2669         return;
2670     }
2671 
2672     const char* us = strchr(name, '_');
2673     size_t len = us ? (size_t)(us - name) : strlen(name);
2674 
2675     if (len == 8 && strncmp(name, "POSITION", 8) == 0)
2676     {
2677         *out_type = cgltf_attribute_type_position;
2678     }
2679     else if (len == 6 && strncmp(name, "NORMAL", 6) == 0)
2680     {
2681         *out_type = cgltf_attribute_type_normal;
2682     }
2683     else if (len == 7 && strncmp(name, "TANGENT", 7) == 0)
2684     {
2685         *out_type = cgltf_attribute_type_tangent;
2686     }
2687     else if (len == 8 && strncmp(name, "TEXCOORD", 8) == 0)
2688     {
2689         *out_type = cgltf_attribute_type_texcoord;
2690     }
2691     else if (len == 5 && strncmp(name, "COLOR", 5) == 0)
2692     {
2693         *out_type = cgltf_attribute_type_color;
2694     }
2695     else if (len == 6 && strncmp(name, "JOINTS", 6) == 0)
2696     {
2697         *out_type = cgltf_attribute_type_joints;
2698     }
2699     else if (len == 7 && strncmp(name, "WEIGHTS", 7) == 0)
2700     {
2701         *out_type = cgltf_attribute_type_weights;
2702     }
2703     else
2704     {
2705         *out_type = cgltf_attribute_type_invalid;
2706     }
2707 
2708     if (us && *out_type != cgltf_attribute_type_invalid)
2709     {
2710         *out_index = CGLTF_ATOI(us + 1);
2711     }
2712 }
2713 
2714 static int cgltf_parse_json_attribute_list(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_attribute** out_attributes, cgltf_size* out_attributes_count)
2715 {
2716     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2717 
2718     if (*out_attributes)
2719     {
2720         return CGLTF_ERROR_JSON;
2721     }
2722 
2723     *out_attributes_count = tokens[i].size;
2724     *out_attributes = (cgltf_attribute*)cgltf_calloc(options, sizeof(cgltf_attribute), *out_attributes_count);
2725     ++i;
2726 
2727     if (!*out_attributes)
2728     {
2729         return CGLTF_ERROR_NOMEM;
2730     }
2731 
2732     for (cgltf_size j = 0; j < *out_attributes_count; ++j)
2733     {
2734         CGLTF_CHECK_KEY(tokens[i]);
2735 
2736         i = cgltf_parse_json_string(options, tokens, i, json_chunk, &(*out_attributes)[j].name);
2737         if (i < 0)
2738         {
2739             return CGLTF_ERROR_JSON;
2740         }
2741 
2742         cgltf_parse_attribute_type((*out_attributes)[j].name, &(*out_attributes)[j].type, &(*out_attributes)[j].index);
2743 
2744         (*out_attributes)[j].data = CGLTF_PTRINDEX(cgltf_accessor, cgltf_json_to_int(tokens + i, json_chunk));
2745         ++i;
2746     }
2747 
2748     return i;
2749 }
2750 
2751 static int cgltf_parse_json_extras(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_extras* out_extras)
2752 {
2753     if (out_extras->data)
2754     {
2755         return CGLTF_ERROR_JSON;
2756     }
2757 
2758     /* fill deprecated fields for now, this will be removed in the future */
2759     out_extras->start_offset = tokens[i].start;
2760     out_extras->end_offset = tokens[i].end;
2761 
2762     size_t start = tokens[i].start;
2763     size_t size = tokens[i].end - start;
2764     out_extras->data = (char*)options->memory.alloc_func(options->memory.user_data, size + 1);
2765     if (!out_extras->data)
2766     {
2767         return CGLTF_ERROR_NOMEM;
2768     }
2769     strncpy(out_extras->data, (const char*)json_chunk + start, size);
2770     out_extras->data[size] = '\0';
2771 
2772     i = cgltf_skip_json(tokens, i);
2773     return i;
2774 }
2775 
2776 static int cgltf_parse_json_unprocessed_extension(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_extension* out_extension)
2777 {
2778     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_STRING);
2779     CGLTF_CHECK_TOKTYPE(tokens[i+1], JSMN_OBJECT);
2780     if (out_extension->name)
2781     {
2782         return CGLTF_ERROR_JSON;
2783     }
2784 
2785     cgltf_size name_length = tokens[i].end - tokens[i].start;
2786     out_extension->name = (char*)options->memory.alloc_func(options->memory.user_data, name_length + 1);
2787     if (!out_extension->name)
2788     {
2789         return CGLTF_ERROR_NOMEM;
2790     }
2791     strncpy(out_extension->name, (const char*)json_chunk + tokens[i].start, name_length);
2792     out_extension->name[name_length] = 0;
2793     i++;
2794 
2795     size_t start = tokens[i].start;
2796     size_t size = tokens[i].end - start;
2797     out_extension->data = (char*)options->memory.alloc_func(options->memory.user_data, size + 1);
2798     if (!out_extension->data)
2799     {
2800         return CGLTF_ERROR_NOMEM;
2801     }
2802     strncpy(out_extension->data, (const char*)json_chunk + start, size);
2803     out_extension->data[size] = '\0';
2804 
2805     i = cgltf_skip_json(tokens, i);
2806 
2807     return i;
2808 }
2809 
2810 static int cgltf_parse_json_unprocessed_extensions(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_size* out_extensions_count, cgltf_extension** out_extensions)
2811 {
2812     ++i;
2813 
2814     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2815     if(*out_extensions)
2816     {
2817         return CGLTF_ERROR_JSON;
2818     }
2819 
2820     int extensions_size = tokens[i].size;
2821     *out_extensions_count = 0;
2822     *out_extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
2823 
2824     if (!*out_extensions)
2825     {
2826         return CGLTF_ERROR_NOMEM;
2827     }
2828 
2829     ++i;
2830 
2831     for (int j = 0; j < extensions_size; ++j)
2832     {
2833         CGLTF_CHECK_KEY(tokens[i]);
2834 
2835         cgltf_size extension_index = (*out_extensions_count)++;
2836         cgltf_extension* extension = &((*out_extensions)[extension_index]);
2837         i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, extension);
2838 
2839         if (i < 0)
2840         {
2841             return i;
2842         }
2843     }
2844     return i;
2845 }
