2847 static int cgltf_parse_json_draco_mesh_compression(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_draco_mesh_compression* out_draco_mesh_compression)
2848 {
2849     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2850 
2851     int size = tokens[i].size;
2852     ++i;
2853 
2854     for (int j = 0; j < size; ++j)
2855     {
2856         CGLTF_CHECK_KEY(tokens[i]);
2857 
2858         if (cgltf_json_strcmp(tokens + i, json_chunk, "attributes") == 0)
2859         {
2860             i = cgltf_parse_json_attribute_list(options, tokens, i + 1, json_chunk, &out_draco_mesh_compression->attributes, &out_draco_mesh_compression->attributes_count);
2861         }
2862         else if (cgltf_json_strcmp(tokens + i, json_chunk, "bufferView") == 0)
2863         {
2864             ++i;
2865             out_draco_mesh_compression->buffer_view = CGLTF_PTRINDEX(cgltf_buffer_view, cgltf_json_to_int(tokens + i, json_chunk));
2866             ++i;
2867         }
2868 
2869         if (i < 0)
2870         {
2871             return i;
2872         }
2873     }
2874 
2875     return i;
2876 }
