2878 static int cgltf_parse_json_mesh_gpu_instancing(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_mesh_gpu_instancing* out_mesh_gpu_instancing)
2879 {
2880     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2881 
2882     int size = tokens[i].size;
2883     ++i;
2884 
2885     for (int j = 0; j < size; ++j)
2886     {
2887         CGLTF_CHECK_KEY(tokens[i]);
2888 
2889         if (cgltf_json_strcmp(tokens + i, json_chunk, "attributes") == 0)
2890         {
2891             i = cgltf_parse_json_attribute_list(options, tokens, i + 1, json_chunk, &out_mesh_gpu_instancing->attributes, &out_mesh_gpu_instancing->attributes_count);
2892         }
2893         else if (cgltf_json_strcmp(tokens + i, json_chunk, "bufferView") == 0)
2894         {
2895             ++i;
2896             out_mesh_gpu_instancing->buffer_view = CGLTF_PTRINDEX(cgltf_buffer_view, cgltf_json_to_int(tokens + i, json_chunk));
2897             ++i;
2898         }
2899 
2900         if (i < 0)
2901         {
2902             return i;
2903         }
2904     }
2905 
2906     return i;
2907 }
2908 
2909 static int cgltf_parse_json_material_mapping_data(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_material_mapping* out_mappings, cgltf_size* offset)
2910 {
2911     (void)options;
2912     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_ARRAY);
2913 
2914     int size = tokens[i].size;
2915     ++i;
2916 
2917     for (int j = 0; j < size; ++j)
2918     {
2919         CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2920 
2921         int obj_size = tokens[i].size;
2922         ++i;
2923 
2924         int material = -1;
2925         int variants_tok = -1;
2926         int extras_tok = -1;
2927 
2928         for (int k = 0; k < obj_size; ++k)
2929         {
2930             CGLTF_CHECK_KEY(tokens[i]);
2931 
2932             if (cgltf_json_strcmp(tokens + i, json_chunk, "material") == 0)
2933             {
2934                 ++i;
2935                 material = cgltf_json_to_int(tokens + i, json_chunk);
2936                 ++i;
2937             }
2938             else if (cgltf_json_strcmp(tokens + i, json_chunk, "variants") == 0)
2939             {
2940                 variants_tok = i+1;
2941                 CGLTF_CHECK_TOKTYPE(tokens[variants_tok], JSMN_ARRAY);
2942 
2943                 i = cgltf_skip_json(tokens, i+1);
2944             }
2945             else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
2946             {
2947                 extras_tok = i + 1;
2948                 i = cgltf_skip_json(tokens, extras_tok);
2949             }
2950             else
2951             {
2952                 i = cgltf_skip_json(tokens, i+1);
2953             }
2954 
2955             if (i < 0)
2956             {
2957                 return i;
2958             }
2959         }
2960 
2961         if (material < 0 || variants_tok < 0)
2962         {
2963             return CGLTF_ERROR_JSON;
2964         }
2965 
2966         if (out_mappings)
2967         {
2968             for (int k = 0; k < tokens[variants_tok].size; ++k)
2969             {
2970                 int variant = cgltf_json_to_int(&tokens[variants_tok + 1 + k], json_chunk);
2971                 if (variant < 0)
2972                     return variant;
2973 
2974                 out_mappings[*offset].material = CGLTF_PTRINDEX(cgltf_material, material);
2975                 out_mappings[*offset].variant = variant;
2976 
2977                 if (extras_tok >= 0)
2978                 {
2979                     int e = cgltf_parse_json_extras(options, tokens, extras_tok, json_chunk, &out_mappings[*offset].extras);
2980                     if (e < 0)
2981                         return e;
2982                 }
2983 
2984                 (*offset)++;
2985             }
2986         }
2987         else
2988         {
2989             (*offset) += tokens[variants_tok].size;
2990         }
2991     }
2992 
2993     return i;
2994 }
2995 
2996 static int cgltf_parse_json_material_mappings(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_primitive* out_prim)
2997 {
2998     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
2999 
3000     int size = tokens[i].size;
3001     ++i;
3002 
3003     for (int j = 0; j < size; ++j)
3004     {
3005         CGLTF_CHECK_KEY(tokens[i]);
3006 
3007         if (cgltf_json_strcmp(tokens + i, json_chunk, "mappings") == 0)
3008         {
3009             if (out_prim->mappings)
3010             {
3011                 return CGLTF_ERROR_JSON;
3012             }
3013 
3014             cgltf_size mappings_offset = 0;
3015             int k = cgltf_parse_json_material_mapping_data(options, tokens, i + 1, json_chunk, NULL, &mappings_offset);
3016             if (k < 0)
3017             {
3018                 return k;
3019             }
3020 
3021             out_prim->mappings_count = mappings_offset;
3022             out_prim->mappings = (cgltf_material_mapping*)cgltf_calloc(options, sizeof(cgltf_material_mapping), out_prim->mappings_count);
3023 
3024             mappings_offset = 0;
3025             i = cgltf_parse_json_material_mapping_data(options, tokens, i + 1, json_chunk, out_prim->mappings, &mappings_offset);
3026         }
3027         else
3028         {
3029             i = cgltf_skip_json(tokens, i+1);
3030         }
3031 
3032         if (i < 0)
3033         {
3034             return i;
3035         }
3036     }
3037 
3038     return i;
3039 }
3040 
3041 static int cgltf_parse_json_primitive(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_primitive* out_prim)
3042 {
3043     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3044 
3045     out_prim->type = cgltf_primitive_type_triangles;
3046 
3047     int size = tokens[i].size;
3048     ++i;
3049 
3050     for (int j = 0; j < size; ++j)
3051     {
3052         CGLTF_CHECK_KEY(tokens[i]);
3053 
3054         if (cgltf_json_strcmp(tokens+i, json_chunk, "mode") == 0)
3055         {
3056             ++i;
3057             out_prim->type
3058                     = (cgltf_primitive_type)
3059                     cgltf_json_to_int(tokens+i, json_chunk);
3060             ++i;
3061         }
3062         else if (cgltf_json_strcmp(tokens+i, json_chunk, "indices") == 0)
3063         {
3064             ++i;
3065             out_prim->indices = CGLTF_PTRINDEX(cgltf_accessor, cgltf_json_to_int(tokens + i, json_chunk));
3066             ++i;
3067         }
3068         else if (cgltf_json_strcmp(tokens+i, json_chunk, "material") == 0)
3069         {
3070             ++i;
3071             out_prim->material = CGLTF_PTRINDEX(cgltf_material, cgltf_json_to_int(tokens + i, json_chunk));
3072             ++i;
3073         }
3074         else if (cgltf_json_strcmp(tokens+i, json_chunk, "attributes") == 0)
3075         {
3076             i = cgltf_parse_json_attribute_list(options, tokens, i + 1, json_chunk, &out_prim->attributes, &out_prim->attributes_count);
3077         }
3078         else if (cgltf_json_strcmp(tokens+i, json_chunk, "targets") == 0)
3079         {
3080             i = cgltf_parse_json_array(options, tokens, i + 1, json_chunk, sizeof(cgltf_morph_target), (void**)&out_prim->targets, &out_prim->targets_count);
3081             if (i < 0)
3082             {
3083                 return i;
3084             }
3085 
3086             for (cgltf_size k = 0; k < out_prim->targets_count; ++k)
3087             {
3088                 i = cgltf_parse_json_attribute_list(options, tokens, i, json_chunk, &out_prim->targets[k].attributes, &out_prim->targets[k].attributes_count);
3089                 if (i < 0)
3090                 {
3091                     return i;
3092                 }
3093             }
3094         }
3095         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
3096         {
3097             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_prim->extras);
3098         }
3099         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
3100         {
3101             ++i;
3102 
3103             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3104             if(out_prim->extensions)
3105             {
3106                 return CGLTF_ERROR_JSON;
3107             }
3108 
3109             int extensions_size = tokens[i].size;
3110             out_prim->extensions_count = 0;
3111             out_prim->extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
3112 
3113             if (!out_prim->extensions)
3114             {
3115                 return CGLTF_ERROR_NOMEM;
3116             }
3117 
3118             ++i;
3119             for (int k = 0; k < extensions_size; ++k)
3120             {
3121                 CGLTF_CHECK_KEY(tokens[i]);
3122 
3123                 if (cgltf_json_strcmp(tokens+i, json_chunk, "KHR_draco_mesh_compression") == 0)
3124                 {
3125                     out_prim->has_draco_mesh_compression = 1;
3126                     i = cgltf_parse_json_draco_mesh_compression(options, tokens, i + 1, json_chunk, &out_prim->draco_mesh_compression);
3127                 }
3128                 else if (cgltf_json_strcmp(tokens+i, json_chunk, "KHR_materials_variants") == 0)
3129                 {
3130                     i = cgltf_parse_json_material_mappings(options, tokens, i + 1, json_chunk, out_prim);
3131                 }
3132                 else
3133                 {
3134                     i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, &(out_prim->extensions[out_prim->extensions_count++]));
3135                 }
3136 
3137                 if (i < 0)
3138                 {
3139                     return i;
3140                 }
3141             }
3142         }
3143         else
3144         {
3145             i = cgltf_skip_json(tokens, i+1);
3146         }
3147 
3148         if (i < 0)
3149         {
3150             return i;
3151         }
3152     }
3153 
3154     return i;
3155 }
3156 
3157 static int cgltf_parse_json_mesh(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_mesh* out_mesh)
3158 {
3159     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3160 
3161     int size = tokens[i].size;
3162     ++i;
3163 
3164     for (int j = 0; j < size; ++j)
3165     {
3166         CGLTF_CHECK_KEY(tokens[i]);
3167 
3168         if (cgltf_json_strcmp(tokens+i, json_chunk, "name") == 0)
3169         {
3170             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_mesh->name);
3171         }
3172         else if (cgltf_json_strcmp(tokens+i, json_chunk, "primitives") == 0)
3173         {
3174             i = cgltf_parse_json_array(options, tokens, i + 1, json_chunk, sizeof(cgltf_primitive), (void**)&out_mesh->primitives, &out_mesh->primitives_count);
3175             if (i < 0)
3176             {
3177                 return i;
3178             }
3179 
3180             for (cgltf_size prim_index = 0; prim_index < out_mesh->primitives_count; ++prim_index)
3181             {
3182                 i = cgltf_parse_json_primitive(options, tokens, i, json_chunk, &out_mesh->primitives[prim_index]);
3183                 if (i < 0)
3184                 {
3185                     return i;
3186                 }
3187             }
3188         }
3189         else if (cgltf_json_strcmp(tokens + i, json_chunk, "weights") == 0)
3190         {
3191             i = cgltf_parse_json_array(options, tokens, i + 1, json_chunk, sizeof(cgltf_float), (void**)&out_mesh->weights, &out_mesh->weights_count);
3192             if (i < 0)
3193             {
3194                 return i;
3195             }
3196 
3197             i = cgltf_parse_json_float_array(tokens, i - 1, json_chunk, out_mesh->weights, (int)out_mesh->weights_count);
3198         }
3199         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
3200         {
3201             ++i;
3202 
3203             out_mesh->extras.start_offset = tokens[i].start;
3204             out_mesh->extras.end_offset = tokens[i].end;
3205 
3206             if (tokens[i].type == JSMN_OBJECT)
3207             {
3208                 int extras_size = tokens[i].size;
3209                 ++i;
3210 
3211                 for (int k = 0; k < extras_size; ++k)
3212                 {
3213                     CGLTF_CHECK_KEY(tokens[i]);
3214 
3215                     if (cgltf_json_strcmp(tokens+i, json_chunk, "targetNames") == 0 && tokens[i+1].type == JSMN_ARRAY)
3216                     {
3217                         i = cgltf_parse_json_string_array(options, tokens, i + 1, json_chunk, &out_mesh->target_names, &out_mesh->target_names_count);
3218                     }
3219                     else
3220                     {
3221                         i = cgltf_skip_json(tokens, i+1);
3222                     }
3223 
3224                     if (i < 0)
3225                     {
3226                         return i;
3227                     }
3228                 }
3229             }
3230             else
3231             {
3232                 i = cgltf_skip_json(tokens, i);
3233             }
3234         }
3235         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
3236         {
3237             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_mesh->extensions_count, &out_mesh->extensions);
3238         }
3239         else
3240         {
3241             i = cgltf_skip_json(tokens, i+1);
3242         }
3243 
3244         if (i < 0)
3245         {
3246             return i;
3247         }
3248     }
3249 
3250     return i;
3251 }
