3253 static int cgltf_parse_json_meshes(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_data* out_data)
3254 {
3255     i = cgltf_parse_json_array(options, tokens, i, json_chunk, sizeof(cgltf_mesh), (void**)&out_data->meshes, &out_data->meshes_count);
3256     if (i < 0)
3257     {
3258         return i;
3259     }
3260 
3261     for (cgltf_size j = 0; j < out_data->meshes_count; ++j)
3262     {
3263         i = cgltf_parse_json_mesh(options, tokens, i, json_chunk, &out_data->meshes[j]);
3264         if (i < 0)
3265         {
3266             return i;
3267         }
3268     }
3269     return i;
3270 }
3271 
3272 static cgltf_component_type cgltf_json_to_component_type(jsmntok_t const* tok, const uint8_t* json_chunk)
3273 {
3274     int type = cgltf_json_to_int(tok, json_chunk);
3275 
3276     switch (type)
3277     {
3278     case 5120:
3279         return cgltf_component_type_r_8;
3280     case 5121:
3281         return cgltf_component_type_r_8u;
3282     case 5122:
3283         return cgltf_component_type_r_16;
3284     case 5123:
3285         return cgltf_component_type_r_16u;
3286     case 5125:
3287         return cgltf_component_type_r_32u;
3288     case 5126:
3289         return cgltf_component_type_r_32f;
3290     default:
3291         return cgltf_component_type_invalid;
3292     }
3293 }
