3426 static int cgltf_parse_json_accessor(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_accessor* out_accessor)
3427 {
3428     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3429 
3430     int size = tokens[i].size;
3431     ++i;
3432 
3433     for (int j = 0; j < size; ++j)
3434     {
3435         CGLTF_CHECK_KEY(tokens[i]);
3436 
3437         if (cgltf_json_strcmp(tokens + i, json_chunk, "name") == 0)
3438         {
3439             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_accessor->name);
3440         }
3441         else if (cgltf_json_strcmp(tokens+i, json_chunk, "bufferView") == 0)
3442         {
3443             ++i;
3444             out_accessor->buffer_view = CGLTF_PTRINDEX(cgltf_buffer_view, cgltf_json_to_int(tokens + i, json_chunk));
3445             ++i;
3446         }
3447         else if (cgltf_json_strcmp(tokens+i, json_chunk, "byteOffset") == 0)
3448         {
3449             ++i;
3450             out_accessor->offset =
3451                     cgltf_json_to_size(tokens+i, json_chunk);
3452             ++i;
3453         }
3454         else if (cgltf_json_strcmp(tokens+i, json_chunk, "componentType") == 0)
3455         {
3456             ++i;
3457             out_accessor->component_type = cgltf_json_to_component_type(tokens + i, json_chunk);
3458             ++i;
3459         }
3460         else if (cgltf_json_strcmp(tokens+i, json_chunk, "normalized") == 0)
3461         {
3462             ++i;
3463             out_accessor->normalized = cgltf_json_to_bool(tokens+i, json_chunk);
3464             ++i;
3465         }
3466         else if (cgltf_json_strcmp(tokens+i, json_chunk, "count") == 0)
3467         {
3468             ++i;
3469             out_accessor->count =
3470                     cgltf_json_to_int(tokens+i, json_chunk);
3471             ++i;
3472         }
3473         else if (cgltf_json_strcmp(tokens+i, json_chunk, "type") == 0)
3474         {
3475             ++i;
3476             if (cgltf_json_strcmp(tokens+i, json_chunk, "SCALAR") == 0)
3477             {
3478                 out_accessor->type = cgltf_type_scalar;
3479             }
3480             else if (cgltf_json_strcmp(tokens+i, json_chunk, "VEC2") == 0)
3481             {
3482                 out_accessor->type = cgltf_type_vec2;
3483             }
3484             else if (cgltf_json_strcmp(tokens+i, json_chunk, "VEC3") == 0)
3485             {
3486                 out_accessor->type = cgltf_type_vec3;
3487             }
3488             else if (cgltf_json_strcmp(tokens+i, json_chunk, "VEC4") == 0)
3489             {
3490                 out_accessor->type = cgltf_type_vec4;
3491             }
3492             else if (cgltf_json_strcmp(tokens+i, json_chunk, "MAT2") == 0)
3493             {
3494                 out_accessor->type = cgltf_type_mat2;
3495             }
3496             else if (cgltf_json_strcmp(tokens+i, json_chunk, "MAT3") == 0)
3497             {
3498                 out_accessor->type = cgltf_type_mat3;
3499             }
3500             else if (cgltf_json_strcmp(tokens+i, json_chunk, "MAT4") == 0)
3501             {
3502                 out_accessor->type = cgltf_type_mat4;
3503             }
3504             ++i;
3505         }
3506         else if (cgltf_json_strcmp(tokens + i, json_chunk, "min") == 0)
3507         {
3508             ++i;
3509             out_accessor->has_min = 1;
3510             // note: we can't parse the precise number of elements since type may not have been computed yet
3511             int min_size = tokens[i].size > 16 ? 16 : tokens[i].size;
3512             i = cgltf_parse_json_float_array(tokens, i, json_chunk, out_accessor->min, min_size);
3513         }
3514         else if (cgltf_json_strcmp(tokens + i, json_chunk, "max") == 0)
3515         {
3516             ++i;
3517             out_accessor->has_max = 1;
3518             // note: we can't parse the precise number of elements since type may not have been computed yet
3519             int max_size = tokens[i].size > 16 ? 16 : tokens[i].size;
3520             i = cgltf_parse_json_float_array(tokens, i, json_chunk, out_accessor->max, max_size);
3521         }
3522         else if (cgltf_json_strcmp(tokens + i, json_chunk, "sparse") == 0)
3523         {
3524             out_accessor->is_sparse = 1;
3525             i = cgltf_parse_json_accessor_sparse(options, tokens, i + 1, json_chunk, &out_accessor->sparse);
3526         }
3527         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
3528         {
3529             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_accessor->extras);
3530         }
3531         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
3532         {
3533             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_accessor->extensions_count, &out_accessor->extensions);
3534         }
3535         else
3536         {
3537             i = cgltf_skip_json(tokens, i+1);
3538         }
3539 
3540         if (i < 0)
3541         {
3542             return i;
3543         }
3544     }
3545 
3546     return i;
3547 }
3548 
3549 static int cgltf_parse_json_texture_transform(jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_texture_transform* out_texture_transform)
3550 {
3551     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3552 
3553     int size = tokens[i].size;
3554     ++i;
3555 
3556     for (int j = 0; j < size; ++j)
3557     {
3558         CGLTF_CHECK_KEY(tokens[i]);
3559 
3560         if (cgltf_json_strcmp(tokens + i, json_chunk, "offset") == 0)
3561         {
3562             i = cgltf_parse_json_float_array(tokens, i + 1, json_chunk, out_texture_transform->offset, 2);
3563         }
3564         else if (cgltf_json_strcmp(tokens + i, json_chunk, "rotation") == 0)
3565         {
3566             ++i;
3567             out_texture_transform->rotation = cgltf_json_to_float(tokens + i, json_chunk);
3568             ++i;
3569         }
3570         else if (cgltf_json_strcmp(tokens + i, json_chunk, "scale") == 0)
3571         {
3572             i = cgltf_parse_json_float_array(tokens, i + 1, json_chunk, out_texture_transform->scale, 2);
3573         }
3574         else if (cgltf_json_strcmp(tokens + i, json_chunk, "texCoord") == 0)
3575         {
3576             ++i;
3577             out_texture_transform->has_texcoord = 1;
3578             out_texture_transform->texcoord = cgltf_json_to_int(tokens + i, json_chunk);
3579             ++i;
3580         }
3581         else
3582         {
3583             i = cgltf_skip_json(tokens, i + 1);
3584         }
3585 
3586         if (i < 0)
3587         {
3588             return i;
3589         }
3590     }
3591 
3592     return i;
3593 }
3594 
3595 static int cgltf_parse_json_texture_view(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_texture_view* out_texture_view)
3596 {
3597     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3598 
3599     out_texture_view->scale = 1.0f;
3600     cgltf_fill_float_array(out_texture_view->transform.scale, 2, 1.0f);
3601 
3602     int size = tokens[i].size;
3603     ++i;
3604 
3605     for (int j = 0; j < size; ++j)
3606     {
3607         CGLTF_CHECK_KEY(tokens[i]);
3608 
3609         if (cgltf_json_strcmp(tokens + i, json_chunk, "index") == 0)
3610         {
3611             ++i;
3612             out_texture_view->texture = CGLTF_PTRINDEX(cgltf_texture, cgltf_json_to_int(tokens + i, json_chunk));
3613             ++i;
3614         }
3615         else if (cgltf_json_strcmp(tokens + i, json_chunk, "texCoord") == 0)
3616         {
3617             ++i;
3618             out_texture_view->texcoord = cgltf_json_to_int(tokens + i, json_chunk);
3619             ++i;
3620         }
3621         else if (cgltf_json_strcmp(tokens + i, json_chunk, "scale") == 0) 
3622         {
3623             ++i;
3624             out_texture_view->scale = cgltf_json_to_float(tokens + i, json_chunk);
3625             ++i;
3626         }
3627         else if (cgltf_json_strcmp(tokens + i, json_chunk, "strength") == 0)
3628         {
3629             ++i;
3630             out_texture_view->scale = cgltf_json_to_float(tokens + i, json_chunk);
3631             ++i;
3632         }
3633         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
3634         {
3635             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_texture_view->extras);
3636         }
3637         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
3638         {
3639             ++i;
3640 
3641             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
3642             if(out_texture_view->extensions)
3643             {
3644                 return CGLTF_ERROR_JSON;
3645             }
3646 
3647             int extensions_size = tokens[i].size;
3648             out_texture_view->extensions_count = 0;
3649             out_texture_view->extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
3650 
3651             if (!out_texture_view->extensions)
3652             {
3653                 return CGLTF_ERROR_NOMEM;
3654             }
3655 
3656             ++i;
3657 
3658             for (int k = 0; k < extensions_size; ++k)
3659             {
3660                 CGLTF_CHECK_KEY(tokens[i]);
3661 
3662                 if (cgltf_json_strcmp(tokens+i, json_chunk, "KHR_texture_transform") == 0)
3663                 {
3664                     out_texture_view->has_transform = 1;
3665                     i = cgltf_parse_json_texture_transform(tokens, i + 1, json_chunk, &out_texture_view->transform);
3666                 }
3667                 else
3668                 {
3669                     i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, &(out_texture_view->extensions[out_texture_view->extensions_count++]));
3670                 }
3671 
3672                 if (i < 0)
3673                 {
3674                     return i;
3675                 }
3676             }
3677         }
3678         else
3679         {
3680             i = cgltf_skip_json(tokens, i + 1);
3681         }
3682 
3683         if (i < 0)
3684         {
3685             return i;
3686         }
3687     }
3688 
3689     return i;
3690 }
