4145             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_image->uri);
4146         }
4147         else if (cgltf_json_strcmp(tokens+i, json_chunk, "bufferView") == 0)
4148         {
4149             ++i;
4150             out_image->buffer_view = CGLTF_PTRINDEX(cgltf_buffer_view, cgltf_json_to_int(tokens + i, json_chunk));
4151             ++i;
4152         }
4153         else if (cgltf_json_strcmp(tokens + i, json_chunk, "mimeType") == 0)
4154         {
4155             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_image->mime_type);
4156         }
4157         else if (cgltf_json_strcmp(tokens + i, json_chunk, "name") == 0)
4158         {
4159             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_image->name);
4160         }
4161         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
4162         {
4163             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_image->extras);
4164         }
4165         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
4166         {
4167             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_image->extensions_count, &out_image->extensions);
4168         }
4169         else
4170         {
4171             i = cgltf_skip_json(tokens, i + 1);
4172         }
4173 
4174         if (i < 0)
4175         {
4176             return i;
4177         }
4178     }
4179 
4180     return i;
