4252 static int cgltf_parse_json_texture(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_texture* out_texture)
4253 {
4254     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4255 
4256     int size = tokens[i].size;
4257     ++i;
4258 
4259     for (int j = 0; j < size; ++j)
4260     {
4261         CGLTF_CHECK_KEY(tokens[i]);
4262 
4263         if (cgltf_json_strcmp(tokens+i, json_chunk, "name") == 0)
4264         {
4265             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_texture->name);
4266         }
4267         else if (cgltf_json_strcmp(tokens + i, json_chunk, "sampler") == 0)
4268         {
4269             ++i;
4270             out_texture->sampler = CGLTF_PTRINDEX(cgltf_sampler, cgltf_json_to_int(tokens + i, json_chunk));
4271             ++i;
4272         }
4273         else if (cgltf_json_strcmp(tokens + i, json_chunk, "source") == 0) 
4274         {
4275             ++i;
4276             out_texture->image = CGLTF_PTRINDEX(cgltf_image, cgltf_json_to_int(tokens + i, json_chunk));
4277             ++i;
4278         }
4279         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
4280         {
4281             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_texture->extras);
4282         }
4283         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
4284         {
4285             ++i;
4286 
4287             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4288             if (out_texture->extensions)
4289             {
4290                 return CGLTF_ERROR_JSON;
4291             }
4292 
4293             int extensions_size = tokens[i].size;
4294             ++i;
4295             out_texture->extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
4296             out_texture->extensions_count = 0;
4297 
4298             if (!out_texture->extensions)
4299             {
4300                 return CGLTF_ERROR_NOMEM;
4301             }
4302 
4303             for (int k = 0; k < extensions_size; ++k)
4304             {
4305                 CGLTF_CHECK_KEY(tokens[i]);
4306 
4307                 if (cgltf_json_strcmp(tokens + i, json_chunk, "KHR_texture_basisu") == 0)
4308                 {
4309                     out_texture->has_basisu = 1;
4310                     ++i;
4311                     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4312                     int num_properties = tokens[i].size;
4313                     ++i;
4314 
4315                     for (int t = 0; t < num_properties; ++t)
4316                     {
4317                         CGLTF_CHECK_KEY(tokens[i]);
4318 
4319                         if (cgltf_json_strcmp(tokens + i, json_chunk, "source") == 0)
4320                         {
4321                             ++i;
4322                             out_texture->basisu_image = CGLTF_PTRINDEX(cgltf_image, cgltf_json_to_int(tokens + i, json_chunk));
4323                             ++i;
4324                         }
4325                         else
4326                         {
4327                             i = cgltf_skip_json(tokens, i + 1);
4328                         }
4329                         if (i < 0)
4330                         {
4331                             return i;
4332                         }
4333                     }
4334                 }
4335                 else
4336                 {
4337                     i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, &(out_texture->extensions[out_texture->extensions_count++]));
4338                 }
4339 
4340                 if (i < 0)
4341                 {
4342                     return i;
4343                 }
4344             }
4345         }
4346         else
4347         {
4348             i = cgltf_skip_json(tokens, i + 1);
4349         }
4350 
4351         if (i < 0)
4352         {
4353             return i;
4354         }
4355     }
4356 
4357     return i;
4358 }
