4640 static int cgltf_parse_json_meshopt_compression(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_meshopt_compression* out_meshopt_compression)
4641 {
4642     (void)options;
4643     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4644 
4645     int size = tokens[i].size;
4646     ++i;
