4671         {
4672             ++i;
4673             out_meshopt_compression->stride = cgltf_json_to_size(tokens+i, json_chunk);
4674             ++i;
4675         }
4676         else if (cgltf_json_strcmp(tokens+i, json_chunk, "count") == 0)
4677         {
4678             ++i;
4679             out_meshopt_compression->count = cgltf_json_to_int(tokens+i, json_chunk);
4680             ++i;
