4682         else if (cgltf_json_strcmp(tokens+i, json_chunk, "mode") == 0)
4683         {
4684             ++i;
4685             if (cgltf_json_strcmp(tokens+i, json_chunk, "ATTRIBUTES") == 0)
4686             {
4687                 out_meshopt_compression->mode = cgltf_meshopt_compression_mode_attributes;
4688             }
4689             else if (cgltf_json_strcmp(tokens+i, json_chunk, "TRIANGLES") == 0)
4690             {
4691                 out_meshopt_compression->mode = cgltf_meshopt_compression_mode_triangles;
4692             }
4693             else if (cgltf_json_strcmp(tokens+i, json_chunk, "INDICES") == 0)
4694             {
4695                 out_meshopt_compression->mode = cgltf_meshopt_compression_mode_indices;
4696             }
4697             ++i;
4698         }
4699         else if (cgltf_json_strcmp(tokens+i, json_chunk, "filter") == 0)
4700         {
4701             ++i;
4702             if (cgltf_json_strcmp(tokens+i, json_chunk, "NONE") == 0)
4703             {
4704                 out_meshopt_compression->filter = cgltf_meshopt_compression_filter_none;
4705             }
4706             else if (cgltf_json_strcmp(tokens+i, json_chunk, "OCTAHEDRAL") == 0)
4707             {
4708                 out_meshopt_compression->filter = cgltf_meshopt_compression_filter_octahedral;
4709             }
4710             else if (cgltf_json_strcmp(tokens+i, json_chunk, "QUATERNION") == 0)
4711             {
4712                 out_meshopt_compression->filter = cgltf_meshopt_compression_filter_quaternion;
4713             }
4714             else if (cgltf_json_strcmp(tokens+i, json_chunk, "EXPONENTIAL") == 0)
4715             {
4716                 out_meshopt_compression->filter = cgltf_meshopt_compression_filter_exponential;
4717             }
4718             ++i;
4719         }
4720         else
4721         {
4722             i = cgltf_skip_json(tokens, i+1);
4723         }
4724 
4725         if (i < 0)
4726         {
4727             return i;
4728         }
4729     }
4730 
4731     return i;
4732 }
4733 
4734 static int cgltf_parse_json_buffer_view(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_buffer_view* out_buffer_view)
4735 {
4736     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4737 
4738     int size = tokens[i].size;
4739     ++i;
4740 
4741     for (int j = 0; j < size; ++j)
4742     {
4743         CGLTF_CHECK_KEY(tokens[i]);
4744 
4745         if (cgltf_json_strcmp(tokens + i, json_chunk, "name") == 0)
4746         {
4747             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_buffer_view->name);
4748         }
4749         else if (cgltf_json_strcmp(tokens+i, json_chunk, "buffer") == 0)
4750         {
4751             ++i;
4752             out_buffer_view->buffer = CGLTF_PTRINDEX(cgltf_buffer, cgltf_json_to_int(tokens + i, json_chunk));
4753             ++i;
4754         }
4755         else if (cgltf_json_strcmp(tokens+i, json_chunk, "byteOffset") == 0)
4756         {
4757             ++i;
4758             out_buffer_view->offset =
4759                     cgltf_json_to_size(tokens+i, json_chunk);
4760             ++i;
4761         }
4762         else if (cgltf_json_strcmp(tokens+i, json_chunk, "byteLength") == 0)
4763         {
4764             ++i;
4765             out_buffer_view->size =
4766                     cgltf_json_to_size(tokens+i, json_chunk);
4767             ++i;
4768         }
4769         else if (cgltf_json_strcmp(tokens+i, json_chunk, "byteStride") == 0)
4770         {
4771             ++i;
4772             out_buffer_view->stride =
4773                     cgltf_json_to_size(tokens+i, json_chunk);
4774             ++i;
4775         }
4776         else if (cgltf_json_strcmp(tokens+i, json_chunk, "target") == 0)
4777         {
4778             ++i;
4779             int type = cgltf_json_to_int(tokens+i, json_chunk);
4780             switch (type)
4781             {
4782             case 34962:
4783                 type = cgltf_buffer_view_type_vertices;
4784                 break;
4785             case 34963:
4786                 type = cgltf_buffer_view_type_indices;
4787                 break;
4788             default:
4789                 type = cgltf_buffer_view_type_invalid;
4790                 break;
4791             }
4792             out_buffer_view->type = (cgltf_buffer_view_type)type;
4793             ++i;
4794         }
4795         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
4796         {
4797             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_buffer_view->extras);
4798         }
4799         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
4800         {
4801             ++i;
4802 
4803             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4804             if(out_buffer_view->extensions)
4805             {
4806                 return CGLTF_ERROR_JSON;
4807             }
4808 
4809             int extensions_size = tokens[i].size;
4810             out_buffer_view->extensions_count = 0;
4811             out_buffer_view->extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
4812 
4813             if (!out_buffer_view->extensions)
4814             {
4815                 return CGLTF_ERROR_NOMEM;
4816             }
4817 
4818             ++i;
4819             for (int k = 0; k < extensions_size; ++k)
4820             {
4821                 CGLTF_CHECK_KEY(tokens[i]);
4822 
4823                 if (cgltf_json_strcmp(tokens+i, json_chunk, "EXT_meshopt_compression") == 0)
4824                 {
4825                     out_buffer_view->has_meshopt_compression = 1;
4826                     i = cgltf_parse_json_meshopt_compression(options, tokens, i + 1, json_chunk, &out_buffer_view->meshopt_compression);
4827                 }
4828                 else
4829                 {
4830                     i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, &(out_buffer_view->extensions[out_buffer_view->extensions_count++]));
4831                 }
4832 
4833                 if (i < 0)
4834                 {
4835                     return i;
4836                 }
4837             }
4838         }
4839         else
4840         {
4841             i = cgltf_skip_json(tokens, i+1);
4842         }
4843 
4844         if (i < 0)
4845         {
4846             return i;
4847         }
4848     }
4849 
4850     return i;
4851 }
