4853 static int cgltf_parse_json_buffer_views(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_data* out_data)
4854 {
4855     i = cgltf_parse_json_array(options, tokens, i, json_chunk, sizeof(cgltf_buffer_view), (void**)&out_data->buffer_views, &out_data->buffer_views_count);
4856     if (i < 0)
4857     {
4858         return i;
4859     }
4860 
4861     for (cgltf_size j = 0; j < out_data->buffer_views_count; ++j)
4862     {
