4872 static int cgltf_parse_json_buffer(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_buffer* out_buffer)
4873 {
4874     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
4875 
4876     int size = tokens[i].size;
4877     ++i;
4878 
4879     for (int j = 0; j < size; ++j)
4880     {
4881         CGLTF_CHECK_KEY(tokens[i]);
4882 
4883         if (cgltf_json_strcmp(tokens + i, json_chunk, "name") == 0)
4884         {
4885             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_buffer->name);
4886         }
4887         else if (cgltf_json_strcmp(tokens+i, json_chunk, "byteLength") == 0)
4888         {
4889             ++i;
4890             out_buffer->size =
4891                     cgltf_json_to_size(tokens+i, json_chunk);
4892             ++i;
4893         }
4894         else if (cgltf_json_strcmp(tokens+i, json_chunk, "uri") == 0)
4895         {
4896             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_buffer->uri);
4897         }
4898         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
4899         {
4900             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_buffer->extras);
4901         }
4902         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
4903         {
4904             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_buffer->extensions_count, &out_buffer->extensions);
4905         }
4906         else
4907         {
4908             i = cgltf_skip_json(tokens, i+1);
4909         }
4910 
4911         if (i < 0)
4912         {
4913             return i;
4914         }
4915     }
4916 
4917     return i;
4918 }
