5256         {
5257             ++i;
5258 
5259             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5260 
5261             int data_size = tokens[i].size;
5262             ++i;
5263 
5264             for (int k = 0; k < data_size; ++k)
5265             {
5266                 CGLTF_CHECK_KEY(tokens[i]);
5267 
5268                 if (cgltf_json_strcmp(tokens+i, json_chunk, "innerConeAngle") == 0)
5269                 {
5270                     ++i;
5271                     out_light->spot_inner_cone_angle = cgltf_json_to_float(tokens + i, json_chunk);
5272                     ++i;
5273                 }
5274                 else if (cgltf_json_strcmp(tokens+i, json_chunk, "outerConeAngle") == 0)
5275                 {
5276                     ++i;
5277                     out_light->spot_outer_cone_angle = cgltf_json_to_float(tokens + i, json_chunk);
5278                     ++i;
5279                 }
5280                 else
5281                 {
5282                     i = cgltf_skip_json(tokens, i+1);
5283                 }
5284 
5285                 if (i < 0)
5286                 {
5287                     return i;
5288                 }
5289             }
5290         }
5291         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
5292         {
5293             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_light->extras);
5294         }
5295         else
5296         {
5297             i = cgltf_skip_json(tokens, i+1);
5298         }
5299 
5300         if (i < 0)
5301         {
5302             return i;
5303         }
5304     }
5305 
5306     return i;
5307 }
