5328 static int cgltf_parse_json_node(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_node* out_node)
5329 {
5330     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5331 
5332     out_node->rotation[3] = 1.0f;
5333     out_node->scale[0] = 1.0f;
5334     out_node->scale[1] = 1.0f;
5335     out_node->scale[2] = 1.0f;
5336     out_node->matrix[0] = 1.0f;
5337     out_node->matrix[5] = 1.0f;
5338     out_node->matrix[10] = 1.0f;
5339     out_node->matrix[15] = 1.0f;
5340 
5341     int size = tokens[i].size;
5342     ++i;
5343 
5344     for (int j = 0; j < size; ++j)
5345     {
5346         CGLTF_CHECK_KEY(tokens[i]);
5347 
5348         if (cgltf_json_strcmp(tokens+i, json_chunk, "name") == 0)
5349         {
5350             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_node->name);
5351         }
5352         else if (cgltf_json_strcmp(tokens+i, json_chunk, "children") == 0)
5353         {
5354             i = cgltf_parse_json_array(options, tokens, i + 1, json_chunk, sizeof(cgltf_node*), (void**)&out_node->children, &out_node->children_count);
5355             if (i < 0)
5356             {
5357                 return i;
5358             }
5359 
5360             for (cgltf_size k = 0; k < out_node->children_count; ++k)
5361             {
5362                 out_node->children[k] = CGLTF_PTRINDEX(cgltf_node, cgltf_json_to_int(tokens + i, json_chunk));
5363                 ++i;
5364             }
5365         }
5366         else if (cgltf_json_strcmp(tokens+i, json_chunk, "mesh") == 0)
5367         {
5368             ++i;
5369             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_PRIMITIVE);
5370             out_node->mesh = CGLTF_PTRINDEX(cgltf_mesh, cgltf_json_to_int(tokens + i, json_chunk));
5371             ++i;
5372         }
5373         else if (cgltf_json_strcmp(tokens+i, json_chunk, "skin") == 0)
5374         {
5375             ++i;
5376             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_PRIMITIVE);
5377             out_node->skin = CGLTF_PTRINDEX(cgltf_skin, cgltf_json_to_int(tokens + i, json_chunk));
5378             ++i;
5379         }
5380         else if (cgltf_json_strcmp(tokens+i, json_chunk, "camera") == 0)
5381         {
5382             ++i;
5383             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_PRIMITIVE);
5384             out_node->camera = CGLTF_PTRINDEX(cgltf_camera, cgltf_json_to_int(tokens + i, json_chunk));
5385             ++i;
5386         }
5387         else if (cgltf_json_strcmp(tokens+i, json_chunk, "translation") == 0)
5388         {
5389             out_node->has_translation = 1;
5390             i = cgltf_parse_json_float_array(tokens, i + 1, json_chunk, out_node->translation, 3);
5391         }
5392         else if (cgltf_json_strcmp(tokens+i, json_chunk, "rotation") == 0)
5393         {
5394             out_node->has_rotation = 1;
5395             i = cgltf_parse_json_float_array(tokens, i + 1, json_chunk, out_node->rotation, 4);
5396         }
5397         else if (cgltf_json_strcmp(tokens+i, json_chunk, "scale") == 0)
5398         {
5399             out_node->has_scale = 1;
5400             i = cgltf_parse_json_float_array(tokens, i + 1, json_chunk, out_node->scale, 3);
5401         }
5402         else if (cgltf_json_strcmp(tokens+i, json_chunk, "matrix") == 0)
5403         {
5404             out_node->has_matrix = 1;
5405             i = cgltf_parse_json_float_array(tokens, i + 1, json_chunk, out_node->matrix, 16);
5406         }
5407         else if (cgltf_json_strcmp(tokens + i, json_chunk, "weights") == 0)
5408         {
5409             i = cgltf_parse_json_array(options, tokens, i + 1, json_chunk, sizeof(cgltf_float), (void**)&out_node->weights, &out_node->weights_count);
5410             if (i < 0)
5411             {
5412                 return i;
5413             }
5414 
5415             i = cgltf_parse_json_float_array(tokens, i - 1, json_chunk, out_node->weights, (int)out_node->weights_count);
5416         }
5417         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
5418         {
5419             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_node->extras);
5420         }
5421         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
5422         {
5423             ++i;
5424 
5425             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5426             if(out_node->extensions)
5427             {
5428                 return CGLTF_ERROR_JSON;
5429             }
5430 
5431             int extensions_size = tokens[i].size;
5432             out_node->extensions_count= 0;
5433             out_node->extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
5434 
5435             if (!out_node->extensions)
5436             {
5437                 return CGLTF_ERROR_NOMEM;
5438             }
5439 
5440             ++i;
5441 
5442             for (int k = 0; k < extensions_size; ++k)
5443             {
5444                 CGLTF_CHECK_KEY(tokens[i]);
5445 
5446                 if (cgltf_json_strcmp(tokens+i, json_chunk, "KHR_lights_punctual") == 0)
5447                 {
5448                     ++i;
5449 
5450                     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5451 
5452                     int data_size = tokens[i].size;
5453                     ++i;
5454 
5455                     for (int m = 0; m < data_size; ++m)
5456                     {
5457                         CGLTF_CHECK_KEY(tokens[i]);
5458 
5459                         if (cgltf_json_strcmp(tokens + i, json_chunk, "light") == 0)
5460                         {
5461                             ++i;
5462                             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_PRIMITIVE);
5463                             out_node->light = CGLTF_PTRINDEX(cgltf_light, cgltf_json_to_int(tokens + i, json_chunk));
5464                             ++i;
5465                         }
5466                         else
5467                         {
5468                             i = cgltf_skip_json(tokens, i + 1);
5469                         }
5470 
5471                         if (i < 0)
5472                         {
5473                             return i;
5474                         }
5475                     }
5476                 }
5477                 else if (cgltf_json_strcmp(tokens + i, json_chunk, "EXT_mesh_gpu_instancing") == 0)
5478                 {
5479                     out_node->has_mesh_gpu_instancing = 1;
5480                     i = cgltf_parse_json_mesh_gpu_instancing(options, tokens, i + 1, json_chunk, &out_node->mesh_gpu_instancing);
5481                 }
5482                 else
5483                 {
5484                     i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, &(out_node->extensions[out_node->extensions_count++]));
5485                 }
5486 
5487                 if (i < 0)
5488                 {
5489                     return i;
5490                 }
5491             }
5492         }
5493         else
5494         {
5495             i = cgltf_skip_json(tokens, i+1);
5496         }
5497 
5498         if (i < 0)
5499         {
5500             return i;
5501         }
5502     }
5503 
5504     return i;
5505 }
