5526 static int cgltf_parse_json_scene(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_scene* out_scene)
5527 {
5528     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5529 
5530     int size = tokens[i].size;
5531     ++i;
5532 
5533     for (int j = 0; j < size; ++j)
5534     {
5535         CGLTF_CHECK_KEY(tokens[i]);
5536 
5537         if (cgltf_json_strcmp(tokens+i, json_chunk, "name") == 0)
5538         {
5539             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_scene->name);
5540         }
5541         else if (cgltf_json_strcmp(tokens+i, json_chunk, "nodes") == 0)
5542         {
5543             i = cgltf_parse_json_array(options, tokens, i + 1, json_chunk, sizeof(cgltf_node*), (void**)&out_scene->nodes, &out_scene->nodes_count);
5544             if (i < 0)
5545             {
5546                 return i;
5547             }
5548 
5549             for (cgltf_size k = 0; k < out_scene->nodes_count; ++k)
5550             {
5551                 out_scene->nodes[k] = CGLTF_PTRINDEX(cgltf_node, cgltf_json_to_int(tokens + i, json_chunk));
5552                 ++i;
5553             }
5554         }
5555         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
5556         {
5557             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_scene->extras);
5558         }
5559         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
5560         {
5561             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_scene->extensions_count, &out_scene->extensions);
5562         }
5563         else
5564         {
5565             i = cgltf_skip_json(tokens, i+1);
5566         }
5567 
5568         if (i < 0)
5569         {
5570             return i;
5571         }
5572     }
5573 
5574     return i;
5575 }
