5641         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
5642         {
5643             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_sampler->extensions_count, &out_sampler->extensions);
5644         }
5645         else
5646         {
5647             i = cgltf_skip_json(tokens, i+1);
5648         }
5649 
5650         if (i < 0)
5651         {
5652             return i;
5653         }
5654     }
5655 
5656     return i;
