5829     for (cgltf_size j = 0; j < out_data->animations_count; ++j)
5830     {
5831         i = cgltf_parse_json_animation(options, tokens, i, json_chunk, &out_data->animations[j]);
5832         if (i < 0)
5833         {
5834             return i;
5835         }
5836     }
5837     return i;
5838 }
5839 
5840 static int cgltf_parse_json_variant(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_material_variant* out_variant)
5841 {
5842     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5843 
5844     int size = tokens[i].size;
5845     ++i;
5846 
5847     for (int j = 0; j < size; ++j)
5848     {
5849         CGLTF_CHECK_KEY(tokens[i]);
5850 
5851         if (cgltf_json_strcmp(tokens+i, json_chunk, "name") == 0)
5852         {
5853             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_variant->name);
5854         }
5855         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
5856         {
5857             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_variant->extras);
5858         }
5859         else
5860         {
5861             i = cgltf_skip_json(tokens, i+1);
5862         }
5863 
5864         if (i < 0)
5865         {
5866             return i;
5867         }
5868     }
5869 
5870     return i;
5871 }
5872 
5873 static int cgltf_parse_json_variants(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_data* out_data)
5874 {
5875     i = cgltf_parse_json_array(options, tokens, i, json_chunk, sizeof(cgltf_material_variant), (void**)&out_data->variants, &out_data->variants_count);
5876     if (i < 0)
5877     {
5878         return i;
5879     }
5880 
5881     for (cgltf_size j = 0; j < out_data->variants_count; ++j)
5882     {
5883         i = cgltf_parse_json_variant(options, tokens, i, json_chunk, &out_data->variants[j]);
5884         if (i < 0)
5885         {
5886             return i;
5887         }
5888     }
5889     return i;
5890 }
5891 
5892 static int cgltf_parse_json_asset(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_asset* out_asset)
5893 {
5894     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
5895 
5896     int size = tokens[i].size;
5897     ++i;
5898 
5899     for (int j = 0; j < size; ++j)
5900     {
5901         CGLTF_CHECK_KEY(tokens[i]);
5902 
5903         if (cgltf_json_strcmp(tokens+i, json_chunk, "copyright") == 0)
5904         {
5905             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_asset->copyright);
5906         }
5907         else if (cgltf_json_strcmp(tokens+i, json_chunk, "generator") == 0)
5908         {
5909             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_asset->generator);
5910         }
5911         else if (cgltf_json_strcmp(tokens+i, json_chunk, "version") == 0)
5912         {
5913             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_asset->version);
5914         }
5915         else if (cgltf_json_strcmp(tokens+i, json_chunk, "minVersion") == 0)
5916         {
5917             i = cgltf_parse_json_string(options, tokens, i + 1, json_chunk, &out_asset->min_version);
5918         }
5919         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extras") == 0)
5920         {
5921             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_asset->extras);
5922         }
5923         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
5924         {
5925             i = cgltf_parse_json_unprocessed_extensions(options, tokens, i, json_chunk, &out_asset->extensions_count, &out_asset->extensions);
5926         }
5927         else
5928         {
5929             i = cgltf_skip_json(tokens, i+1);
5930         }
5931 
5932         if (i < 0)
5933         {
5934             return i;
5935         }
5936     }
5937 
5938     if (out_asset->version && CGLTF_ATOF(out_asset->version) < 2)
5939     {
5940         return CGLTF_ERROR_LEGACY;
5941     }
5942 
5943     return i;
5944 }
5945 
5946 cgltf_size cgltf_num_components(cgltf_type type) {
5947     switch (type)
5948     {
5949     case cgltf_type_vec2:
5950         return 2;
5951     case cgltf_type_vec3:
5952         return 3;
5953     case cgltf_type_vec4:
5954         return 4;
5955     case cgltf_type_mat2:
5956         return 4;
5957     case cgltf_type_mat3:
5958         return 9;
5959     case cgltf_type_mat4:
5960         return 16;
5961     case cgltf_type_invalid:
5962     case cgltf_type_scalar:
5963     default:
5964         return 1;
5965     }
5966 }
5967 
5968 static cgltf_size cgltf_component_size(cgltf_component_type component_type) {
5969     switch (component_type)
5970     {
5971     case cgltf_component_type_r_8:
5972     case cgltf_component_type_r_8u:
5973         return 1;
5974     case cgltf_component_type_r_16:
5975     case cgltf_component_type_r_16u:
5976         return 2;
5977     case cgltf_component_type_r_32u:
5978     case cgltf_component_type_r_32f:
5979         return 4;
5980     case cgltf_component_type_invalid:
5981     default:
5982         return 0;
5983     }
5984 }
5985 
5986 static cgltf_size cgltf_calc_size(cgltf_type type, cgltf_component_type component_type)
5987 {
5988     cgltf_size component_size = cgltf_component_size(component_type);
5989     if (type == cgltf_type_mat2 && component_size == 1)
5990     {
5991         return 8 * component_size;
5992     }
5993     else if (type == cgltf_type_mat3 && (component_size == 1 || component_size == 2))
5994     {
5995         return 12 * component_size;
5996     }
5997     return component_size * cgltf_num_components(type);
5998 }
5999 
6000 static int cgltf_fixup_pointers(cgltf_data* out_data);
6001 
6002 static int cgltf_parse_json_root(cgltf_options* options, jsmntok_t const* tokens, int i, const uint8_t* json_chunk, cgltf_data* out_data)
6003 {
6004     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
6005 
6006     int size = tokens[i].size;
6007     ++i;
6008 
6009     for (int j = 0; j < size; ++j)
6010     {
6011         CGLTF_CHECK_KEY(tokens[i]);
6012 
6013         if (cgltf_json_strcmp(tokens + i, json_chunk, "asset") == 0)
6014         {
6015             i = cgltf_parse_json_asset(options, tokens, i + 1, json_chunk, &out_data->asset);
6016         }
6017         else if (cgltf_json_strcmp(tokens + i, json_chunk, "meshes") == 0)
6018         {
6019             i = cgltf_parse_json_meshes(options, tokens, i + 1, json_chunk, out_data);
6020         }
6021         else if (cgltf_json_strcmp(tokens + i, json_chunk, "accessors") == 0)
6022         {
6023             i = cgltf_parse_json_accessors(options, tokens, i + 1, json_chunk, out_data);
6024         }
6025         else if (cgltf_json_strcmp(tokens + i, json_chunk, "bufferViews") == 0)
6026         {
6027             i = cgltf_parse_json_buffer_views(options, tokens, i + 1, json_chunk, out_data);
6028         }
6029         else if (cgltf_json_strcmp(tokens + i, json_chunk, "buffers") == 0)
6030         {
6031             i = cgltf_parse_json_buffers(options, tokens, i + 1, json_chunk, out_data);
6032         }
6033         else if (cgltf_json_strcmp(tokens + i, json_chunk, "materials") == 0)
6034         {
6035             i = cgltf_parse_json_materials(options, tokens, i + 1, json_chunk, out_data);
6036         }
6037         else if (cgltf_json_strcmp(tokens + i, json_chunk, "images") == 0)
6038         {
6039             i = cgltf_parse_json_images(options, tokens, i + 1, json_chunk, out_data);
6040         }
6041         else if (cgltf_json_strcmp(tokens + i, json_chunk, "textures") == 0)
6042         {
6043             i = cgltf_parse_json_textures(options, tokens, i + 1, json_chunk, out_data);
6044         }
6045         else if (cgltf_json_strcmp(tokens + i, json_chunk, "samplers") == 0)
6046         {
6047             i = cgltf_parse_json_samplers(options, tokens, i + 1, json_chunk, out_data);
6048         }
6049         else if (cgltf_json_strcmp(tokens + i, json_chunk, "skins") == 0)
6050         {
6051             i = cgltf_parse_json_skins(options, tokens, i + 1, json_chunk, out_data);
6052         }
6053         else if (cgltf_json_strcmp(tokens + i, json_chunk, "cameras") == 0)
6054         {
6055             i = cgltf_parse_json_cameras(options, tokens, i + 1, json_chunk, out_data);
6056         }
6057         else if (cgltf_json_strcmp(tokens + i, json_chunk, "nodes") == 0)
6058         {
6059             i = cgltf_parse_json_nodes(options, tokens, i + 1, json_chunk, out_data);
6060         }
6061         else if (cgltf_json_strcmp(tokens + i, json_chunk, "scenes") == 0)
6062         {
6063             i = cgltf_parse_json_scenes(options, tokens, i + 1, json_chunk, out_data);
6064         }
6065         else if (cgltf_json_strcmp(tokens + i, json_chunk, "scene") == 0)
6066         {
6067             ++i;
6068             out_data->scene = CGLTF_PTRINDEX(cgltf_scene, cgltf_json_to_int(tokens + i, json_chunk));
6069             ++i;
6070         }
6071         else if (cgltf_json_strcmp(tokens + i, json_chunk, "animations") == 0)
6072         {
6073             i = cgltf_parse_json_animations(options, tokens, i + 1, json_chunk, out_data);
6074         }
6075         else if (cgltf_json_strcmp(tokens+i, json_chunk, "extras") == 0)
6076         {
6077             i = cgltf_parse_json_extras(options, tokens, i + 1, json_chunk, &out_data->extras);
6078         }
6079         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensions") == 0)
6080         {
6081             ++i;
6082 
6083             CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
6084             if(out_data->data_extensions)
6085             {
6086                 return CGLTF_ERROR_JSON;
6087             }
6088 
6089             int extensions_size = tokens[i].size;
6090             out_data->data_extensions_count = 0;
6091             out_data->data_extensions = (cgltf_extension*)cgltf_calloc(options, sizeof(cgltf_extension), extensions_size);
6092 
6093             if (!out_data->data_extensions)
6094             {
6095                 return CGLTF_ERROR_NOMEM;
6096             }
6097 
6098             ++i;
6099 
6100             for (int k = 0; k < extensions_size; ++k)
6101             {
6102                 CGLTF_CHECK_KEY(tokens[i]);
6103 
6104                 if (cgltf_json_strcmp(tokens+i, json_chunk, "KHR_lights_punctual") == 0)
6105                 {
6106                     ++i;
6107 
6108                     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
6109 
6110                     int data_size = tokens[i].size;
6111                     ++i;
6112 
6113                     for (int m = 0; m < data_size; ++m)
6114                     {
6115                         CGLTF_CHECK_KEY(tokens[i]);
6116 
6117                         if (cgltf_json_strcmp(tokens + i, json_chunk, "lights") == 0)
6118                         {
6119                             i = cgltf_parse_json_lights(options, tokens, i + 1, json_chunk, out_data);
6120                         }
6121                         else
6122                         {
6123                             i = cgltf_skip_json(tokens, i + 1);
6124                         }
6125 
6126                         if (i < 0)
6127                         {
6128                             return i;
6129                         }
6130                     }
6131                 }
6132                 else if (cgltf_json_strcmp(tokens+i, json_chunk, "KHR_materials_variants") == 0)
6133                 {
6134                     ++i;
6135 
6136                     CGLTF_CHECK_TOKTYPE(tokens[i], JSMN_OBJECT);
6137 
6138                     int data_size = tokens[i].size;
6139                     ++i;
6140 
6141                     for (int m = 0; m < data_size; ++m)
6142                     {
6143                         CGLTF_CHECK_KEY(tokens[i]);
6144 
6145                         if (cgltf_json_strcmp(tokens + i, json_chunk, "variants") == 0)
6146                         {
6147                             i = cgltf_parse_json_variants(options, tokens, i + 1, json_chunk, out_data);
6148                         }
6149                         else
6150                         {
6151                             i = cgltf_skip_json(tokens, i + 1);
6152                         }
6153 
6154                         if (i < 0)
6155                         {
6156                             return i;
6157                         }
6158                     }
6159                 }
6160                 else
6161                 {
6162                     i = cgltf_parse_json_unprocessed_extension(options, tokens, i, json_chunk, &(out_data->data_extensions[out_data->data_extensions_count++]));
6163                 }
6164 
6165                 if (i < 0)
6166                 {
6167                     return i;
6168                 }
6169             }
6170         }
6171         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensionsUsed") == 0)
6172         {
6173             i = cgltf_parse_json_string_array(options, tokens, i + 1, json_chunk, &out_data->extensions_used, &out_data->extensions_used_count);
6174         }
6175         else if (cgltf_json_strcmp(tokens + i, json_chunk, "extensionsRequired") == 0)
6176         {
6177             i = cgltf_parse_json_string_array(options, tokens, i + 1, json_chunk, &out_data->extensions_required, &out_data->extensions_required_count);
6178         }
6179         else
6180         {
6181             i = cgltf_skip_json(tokens, i + 1);
6182         }
6183 
6184         if (i < 0)
6185         {
6186             return i;
6187         }
6188     }
6189 
6190     return i;
6191 }
6192 
6193 cgltf_result cgltf_parse_json(cgltf_options* options, const uint8_t* json_chunk, cgltf_size size, cgltf_data** out_data)
6194 {
6195     jsmn_parser parser = { 0, 0, 0 };
6196 
6197     if (options->json_token_count == 0)
6198     {
6199         int token_count = jsmn_parse(&parser, (const char*)json_chunk, size, NULL, 0);
6200 
6201         if (token_count <= 0)
6202         {
6203             return cgltf_result_invalid_json;
6204         }
6205 
6206         options->json_token_count = token_count;
6207     }
6208 
6209     jsmntok_t* tokens = (jsmntok_t*)options->memory.alloc_func(options->memory.user_data, sizeof(jsmntok_t) * (options->json_token_count + 1));
6210 
6211     if (!tokens)
6212     {
6213         return cgltf_result_out_of_memory;
6214     }
6215 
6216     jsmn_init(&parser);
6217 
6218     int token_count = jsmn_parse(&parser, (const char*)json_chunk, size, tokens, options->json_token_count);
6219 
6220     if (token_count <= 0)
6221     {
6222         options->memory.free_func(options->memory.user_data, tokens);
6223         return cgltf_result_invalid_json;
6224     }
6225 
6226     // this makes sure that we always have an UNDEFINED token at the end of the stream
6227     // for invalid JSON inputs this makes sure we don't perform out of bound reads of token data
6228     tokens[token_count].type = JSMN_UNDEFINED;
6229 
6230     cgltf_data* data = (cgltf_data*)options->memory.alloc_func(options->memory.user_data, sizeof(cgltf_data));
6231 
6232     if (!data)
6233     {
6234         options->memory.free_func(options->memory.user_data, tokens);
6235         return cgltf_result_out_of_memory;
6236     }
6237 
6238     memset(data, 0, sizeof(cgltf_data));
6239     data->memory = options->memory;
6240     data->file = options->file;
6241 
6242     int i = cgltf_parse_json_root(options, tokens, 0, json_chunk, data);
6243 
6244     options->memory.free_func(options->memory.user_data, tokens);
6245 
6246     if (i < 0)
6247     {
6248         cgltf_free(data);
6249 
6250         switch (i)
6251         {
6252         case CGLTF_ERROR_NOMEM: return cgltf_result_out_of_memory;
6253         case CGLTF_ERROR_LEGACY: return cgltf_result_legacy_gltf;
6254         default: return cgltf_result_invalid_gltf;
6255         }
6256     }
6257 
6258     if (cgltf_fixup_pointers(data) < 0)
6259     {
6260         cgltf_free(data);
6261         return cgltf_result_invalid_gltf;
6262     }
6263 
6264     data->json = (const char*)json_chunk;
6265     data->json_size = size;
6266 
6267     *out_data = data;
6268 
6269     return cgltf_result_success;
6270 }
6271 
6272 static int cgltf_fixup_pointers(cgltf_data* data)
6273 {
6274     for (cgltf_size i = 0; i < data->meshes_count; ++i)
6275     {
6276         for (cgltf_size j = 0; j < data->meshes[i].primitives_count; ++j)
6277         {
6278             CGLTF_PTRFIXUP(data->meshes[i].primitives[j].indices, data->accessors, data->accessors_count);
6279             CGLTF_PTRFIXUP(data->meshes[i].primitives[j].material, data->materials, data->materials_count);
6280 
6281             for (cgltf_size k = 0; k < data->meshes[i].primitives[j].attributes_count; ++k)
6282             {
6283                 CGLTF_PTRFIXUP_REQ(data->meshes[i].primitives[j].attributes[k].data, data->accessors, data->accessors_count);
6284             }
6285 
6286             for (cgltf_size k = 0; k < data->meshes[i].primitives[j].targets_count; ++k)
6287             {
6288                 for (cgltf_size m = 0; m < data->meshes[i].primitives[j].targets[k].attributes_count; ++m)
6289                 {
6290                     CGLTF_PTRFIXUP_REQ(data->meshes[i].primitives[j].targets[k].attributes[m].data, data->accessors, data->accessors_count);
6291                 }
6292             }
6293 
6294             if (data->meshes[i].primitives[j].has_draco_mesh_compression)
6295             {
6296                 CGLTF_PTRFIXUP_REQ(data->meshes[i].primitives[j].draco_mesh_compression.buffer_view, data->buffer_views, data->buffer_views_count);
6297                 for (cgltf_size m = 0; m < data->meshes[i].primitives[j].draco_mesh_compression.attributes_count; ++m)
6298                 {
6299                     CGLTF_PTRFIXUP_REQ(data->meshes[i].primitives[j].draco_mesh_compression.attributes[m].data, data->accessors, data->accessors_count);
6300                 }
6301             }
6302 
6303             for (cgltf_size k = 0; k < data->meshes[i].primitives[j].mappings_count; ++k)
6304             {
6305                 CGLTF_PTRFIXUP_REQ(data->meshes[i].primitives[j].mappings[k].material, data->materials, data->materials_count);
6306             }
6307         }
6308     }
6309 
6310     for (cgltf_size i = 0; i < data->accessors_count; ++i)
6311     {
6312         CGLTF_PTRFIXUP(data->accessors[i].buffer_view, data->buffer_views, data->buffer_views_count);
6313 
6314         if (data->accessors[i].is_sparse)
6315         {
6316             CGLTF_PTRFIXUP_REQ(data->accessors[i].sparse.indices_buffer_view, data->buffer_views, data->buffer_views_count);
6317             CGLTF_PTRFIXUP_REQ(data->accessors[i].sparse.values_buffer_view, data->buffer_views, data->buffer_views_count);
6318         }
6319 
6320         if (data->accessors[i].buffer_view)
6321         {
6322             data->accessors[i].stride = data->accessors[i].buffer_view->stride;
6323         }
6324 
6325         if (data->accessors[i].stride == 0)
6326         {
6327             data->accessors[i].stride = cgltf_calc_size(data->accessors[i].type, data->accessors[i].component_type);
6328         }
6329     }
6330 
6331     for (cgltf_size i = 0; i < data->textures_count; ++i)
6332     {
6333         CGLTF_PTRFIXUP(data->textures[i].image, data->images, data->images_count);
6334         CGLTF_PTRFIXUP(data->textures[i].basisu_image, data->images, data->images_count);
6335         CGLTF_PTRFIXUP(data->textures[i].sampler, data->samplers, data->samplers_count);
6336     }
6337 
6338     for (cgltf_size i = 0; i < data->images_count; ++i)
6339     {
6340         CGLTF_PTRFIXUP(data->images[i].buffer_view, data->buffer_views, data->buffer_views_count);
6341     }
6342 
6343     for (cgltf_size i = 0; i < data->materials_count; ++i)
6344     {
6345         CGLTF_PTRFIXUP(data->materials[i].normal_texture.texture, data->textures, data->textures_count);
6346         CGLTF_PTRFIXUP(data->materials[i].emissive_texture.texture, data->textures, data->textures_count);
6347         CGLTF_PTRFIXUP(data->materials[i].occlusion_texture.texture, data->textures, data->textures_count);
6348 
6349         CGLTF_PTRFIXUP(data->materials[i].pbr_metallic_roughness.base_color_texture.texture, data->textures, data->textures_count);
6350         CGLTF_PTRFIXUP(data->materials[i].pbr_metallic_roughness.metallic_roughness_texture.texture, data->textures, data->textures_count);
6351 
6352         CGLTF_PTRFIXUP(data->materials[i].pbr_specular_glossiness.diffuse_texture.texture, data->textures, data->textures_count);
6353         CGLTF_PTRFIXUP(data->materials[i].pbr_specular_glossiness.specular_glossiness_texture.texture, data->textures, data->textures_count);
6354 
6355         CGLTF_PTRFIXUP(data->materials[i].clearcoat.clearcoat_texture.texture, data->textures, data->textures_count);
6356         CGLTF_PTRFIXUP(data->materials[i].clearcoat.clearcoat_roughness_texture.texture, data->textures, data->textures_count);
6357         CGLTF_PTRFIXUP(data->materials[i].clearcoat.clearcoat_normal_texture.texture, data->textures, data->textures_count);
6358 
6359         CGLTF_PTRFIXUP(data->materials[i].specular.specular_texture.texture, data->textures, data->textures_count);
6360         CGLTF_PTRFIXUP(data->materials[i].specular.specular_color_texture.texture, data->textures, data->textures_count);
6361 
6362         CGLTF_PTRFIXUP(data->materials[i].transmission.transmission_texture.texture, data->textures, data->textures_count);
6363 
6364         CGLTF_PTRFIXUP(data->materials[i].volume.thickness_texture.texture, data->textures, data->textures_count);
6365 
6366         CGLTF_PTRFIXUP(data->materials[i].sheen.sheen_color_texture.texture, data->textures, data->textures_count);
6367         CGLTF_PTRFIXUP(data->materials[i].sheen.sheen_roughness_texture.texture, data->textures, data->textures_count);
6368 
6369         CGLTF_PTRFIXUP(data->materials[i].iridescence.iridescence_texture.texture, data->textures, data->textures_count);
6370         CGLTF_PTRFIXUP(data->materials[i].iridescence.iridescence_thickness_texture.texture, data->textures, data->textures_count);
6371     }
6372 
6373     for (cgltf_size i = 0; i < data->buffer_views_count; ++i)
6374     {
6375         CGLTF_PTRFIXUP_REQ(data->buffer_views[i].buffer, data->buffers, data->buffers_count);
6376 
6377         if (data->buffer_views[i].has_meshopt_compression)
6378         {
6379             CGLTF_PTRFIXUP_REQ(data->buffer_views[i].meshopt_compression.buffer, data->buffers, data->buffers_count);
6380         }
6381     }
6382 
6383     for (cgltf_size i = 0; i < data->skins_count; ++i)
6384     {
6385         for (cgltf_size j = 0; j < data->skins[i].joints_count; ++j)
6386         {
6387             CGLTF_PTRFIXUP_REQ(data->skins[i].joints[j], data->nodes, data->nodes_count);
6388         }
6389 
6390         CGLTF_PTRFIXUP(data->skins[i].skeleton, data->nodes, data->nodes_count);
6391         CGLTF_PTRFIXUP(data->skins[i].inverse_bind_matrices, data->accessors, data->accessors_count);
6392     }
6393 
6394     for (cgltf_size i = 0; i < data->nodes_count; ++i)
6395     {
6396         for (cgltf_size j = 0; j < data->nodes[i].children_count; ++j)
6397         {
6398             CGLTF_PTRFIXUP_REQ(data->nodes[i].children[j], data->nodes, data->nodes_count);
6399 
6400             if (data->nodes[i].children[j]->parent)
6401             {
6402                 return CGLTF_ERROR_JSON;
6403             }
6404 
6405             data->nodes[i].children[j]->parent = &data->nodes[i];
6406         }
6407 
6408         CGLTF_PTRFIXUP(data->nodes[i].mesh, data->meshes, data->meshes_count);
6409         CGLTF_PTRFIXUP(data->nodes[i].skin, data->skins, data->skins_count);
6410         CGLTF_PTRFIXUP(data->nodes[i].camera, data->cameras, data->cameras_count);
6411         CGLTF_PTRFIXUP(data->nodes[i].light, data->lights, data->lights_count);
6412 
6413         if (data->nodes[i].has_mesh_gpu_instancing)
6414         {
6415             CGLTF_PTRFIXUP_REQ(data->nodes[i].mesh_gpu_instancing.buffer_view, data->buffer_views, data->buffer_views_count);
6416             for (cgltf_size m = 0; m < data->nodes[i].mesh_gpu_instancing.attributes_count; ++m)
6417             {
6418                 CGLTF_PTRFIXUP_REQ(data->nodes[i].mesh_gpu_instancing.attributes[m].data, data->accessors, data->accessors_count);
6419             }
6420         }
6421     }
6422 
6423     for (cgltf_size i = 0; i < data->scenes_count; ++i)
6424     {
6425         for (cgltf_size j = 0; j < data->scenes[i].nodes_count; ++j)
6426         {
6427             CGLTF_PTRFIXUP_REQ(data->scenes[i].nodes[j], data->nodes, data->nodes_count);
6428 
6429             if (data->scenes[i].nodes[j]->parent)
6430             {
6431                 return CGLTF_ERROR_JSON;
6432             }
6433         }
6434     }
6435 
6436     CGLTF_PTRFIXUP(data->scene, data->scenes, data->scenes_count);
6437 
6438     for (cgltf_size i = 0; i < data->animations_count; ++i)
6439     {
6440         for (cgltf_size j = 0; j < data->animations[i].samplers_count; ++j)
6441         {
6442             CGLTF_PTRFIXUP_REQ(data->animations[i].samplers[j].input, data->accessors, data->accessors_count);
6443             CGLTF_PTRFIXUP_REQ(data->animations[i].samplers[j].output, data->accessors, data->accessors_count);
6444         }
6445 
6446         for (cgltf_size j = 0; j < data->animations[i].channels_count; ++j)
6447         {
6448             CGLTF_PTRFIXUP_REQ(data->animations[i].channels[j].sampler, data->animations[i].samplers, data->animations[i].samplers_count);
6449             CGLTF_PTRFIXUP(data->animations[i].channels[j].target_node, data->nodes, data->nodes_count);
6450         }
6451     }
6452 
6453     return 0;
6454 }
