6485 static jsmntok_t *jsmn_alloc_token(jsmn_parser *parser,
6486                    jsmntok_t *tokens, size_t num_tokens) {
6487     jsmntok_t *tok;
6488     if (parser->toknext >= num_tokens) {
6489         return NULL;
6490     }
6491     tok = &tokens[parser->toknext++];
6492     tok->start = tok->end = -1;
6493     tok->size = 0;
6494 #ifdef JSMN_PARENT_LINKS
6495     tok->parent = -1;
6496 #endif
6497     return tok;
6498 }
6499 
6503 static void jsmn_fill_token(jsmntok_t *token, jsmntype_t type,
6504                 ptrdiff_t start, ptrdiff_t end) {
6505     token->type = type;
6506     token->start = start;
6507     token->end = end;
6508     token->size = 0;
6509 }
6510 
6514 static int jsmn_parse_primitive(jsmn_parser *parser, const char *js,
6515                 size_t len, jsmntok_t *tokens, size_t num_tokens) {
6516     jsmntok_t *token;
6517     ptrdiff_t start;
6518 
6519     start = parser->pos;
6520 
6521     for (; parser->pos < len && js[parser->pos] != '\0'; parser->pos++) {
6522         switch (js[parser->pos]) {
6523 #ifndef JSMN_STRICT
6524         /* In strict mode primitive must be followed by "," or "}" or "]" */
6525         case ':':
6526 #endif
6527         case '\t' : case '\r' : case '\n' : case ' ' :
6528         case ','  : case ']'  : case '}' :
6529             goto found;
6530         }
6531         if (js[parser->pos] < 32 || js[parser->pos] >= 127) {
6532             parser->pos = start;
6533             return JSMN_ERROR_INVAL;
6534         }
6535     }
6536 #ifdef JSMN_STRICT
6537     /* In strict mode primitive must be followed by a comma/object/array */
6538     parser->pos = start;
6539     return JSMN_ERROR_PART;
6540 #endif
6541 
6542 found:
6543     if (tokens == NULL) {
6544         parser->pos--;
6545         return 0;
6546     }
6547     token = jsmn_alloc_token(parser, tokens, num_tokens);
6548     if (token == NULL) {
6549         parser->pos = start;
6550         return JSMN_ERROR_NOMEM;
6551     }
6552     jsmn_fill_token(token, JSMN_PRIMITIVE, start, parser->pos);
6553 #ifdef JSMN_PARENT_LINKS
6554     token->parent = parser->toksuper;
6555 #endif
6556     parser->pos--;
6557     return 0;
6558 }
6559 
6563 static int jsmn_parse_string(jsmn_parser *parser, const char *js,
6564                  size_t len, jsmntok_t *tokens, size_t num_tokens) {
6565     jsmntok_t *token;
6566 
6567     ptrdiff_t start = parser->pos;
6568 
6569     parser->pos++;
6570 
6571     /* Skip starting quote */
6572     for (; parser->pos < len && js[parser->pos] != '\0'; parser->pos++) {
6573         char c = js[parser->pos];
6574 
6575         /* Quote: end of string */
6576         if (c == '\"') {
6577             if (tokens == NULL) {
6578                 return 0;
6579             }
6580             token = jsmn_alloc_token(parser, tokens, num_tokens);
6581             if (token == NULL) {
6582                 parser->pos = start;
6583                 return JSMN_ERROR_NOMEM;
6584             }
6585             jsmn_fill_token(token, JSMN_STRING, start+1, parser->pos);
6586 #ifdef JSMN_PARENT_LINKS
6587             token->parent = parser->toksuper;
6588 #endif
6589             return 0;
6590         }
6591 
6592         /* Backslash: Quoted symbol expected */
6593         if (c == '\\' && parser->pos + 1 < len) {
6594             int i;
6595             parser->pos++;
6596             switch (js[parser->pos]) {
6597             /* Allowed escaped symbols */
6598             case '\"': case '/' : case '\\' : case 'b' :
6599             case 'f' : case 'r' : case 'n'  : case 't' :
6600                 break;
6601                 /* Allows escaped symbol \uXXXX */
6602             case 'u':
6603                 parser->pos++;
6604                 for(i = 0; i < 4 && parser->pos < len && js[parser->pos] != '\0'; i++) {
6605                     /* If it isn't a hex character we have an error */
6606                     if(!((js[parser->pos] >= 48 && js[parser->pos] <= 57) || /* 0-9 */
6607                          (js[parser->pos] >= 65 && js[parser->pos] <= 70) || /* A-F */
6608                          (js[parser->pos] >= 97 && js[parser->pos] <= 102))) { /* a-f */
6609                         parser->pos = start;
6610                         return JSMN_ERROR_INVAL;
6611                     }
6612                     parser->pos++;
6613                 }
6614                 parser->pos--;
6615                 break;
6616                 /* Unexpected symbol */
6617             default:
6618                 parser->pos = start;
6619                 return JSMN_ERROR_INVAL;
6620             }
6621         }
6622     }
6623     parser->pos = start;
6624     return JSMN_ERROR_PART;
6625 }
6626 
6630 static int jsmn_parse(jsmn_parser *parser, const char *js, size_t len,
6631            jsmntok_t *tokens, size_t num_tokens) {
6632     int r;
6633     int i;
6634     jsmntok_t *token;
6635     int count = parser->toknext;
6636 
6637     for (; parser->pos < len && js[parser->pos] != '\0'; parser->pos++) {
6638         char c;
6639         jsmntype_t type;
6640 
6641         c = js[parser->pos];
6642         switch (c) {
6643         case '{': case '[':
6644             count++;
6645             if (tokens == NULL) {
6646                 break;
6647             }
6648             token = jsmn_alloc_token(parser, tokens, num_tokens);
6649             if (token == NULL)
6650                 return JSMN_ERROR_NOMEM;
6651             if (parser->toksuper != -1) {
6652                 tokens[parser->toksuper].size++;
6653 #ifdef JSMN_PARENT_LINKS
6654                 token->parent = parser->toksuper;
6655 #endif
6656             }
6657             token->type = (c == '{' ? JSMN_OBJECT : JSMN_ARRAY);
6658             token->start = parser->pos;
6659             parser->toksuper = parser->toknext - 1;
6660             break;
6661         case '}': case ']':
6662             if (tokens == NULL)
6663                 break;
6664             type = (c == '}' ? JSMN_OBJECT : JSMN_ARRAY);
6665 #ifdef JSMN_PARENT_LINKS
6666             if (parser->toknext < 1) {
6667                 return JSMN_ERROR_INVAL;
6668             }
6669             token = &tokens[parser->toknext - 1];
6670             for (;;) {
6671                 if (token->start != -1 && token->end == -1) {
6672                     if (token->type != type) {
6673                         return JSMN_ERROR_INVAL;
6674                     }
6675                     token->end = parser->pos + 1;
6676                     parser->toksuper = token->parent;
6677                     break;
6678                 }
6679                 if (token->parent == -1) {
6680                     if(token->type != type || parser->toksuper == -1) {
6681                         return JSMN_ERROR_INVAL;
6682                     }
6683                     break;
6684                 }
6685                 token = &tokens[token->parent];
6686             }
6687 #else
6688             for (i = parser->toknext - 1; i >= 0; i--) {
6689                 token = &tokens[i];
6690                 if (token->start != -1 && token->end == -1) {
6691                     if (token->type != type) {
6692                         return JSMN_ERROR_INVAL;
6693                     }
6694                     parser->toksuper = -1;
6695                     token->end = parser->pos + 1;
6696                     break;
6697                 }
6698             }
6699             /* Error if unmatched closing bracket */
6700             if (i == -1) return JSMN_ERROR_INVAL;
6701             for (; i >= 0; i--) {
6702                 token = &tokens[i];
6703                 if (token->start != -1 && token->end == -1) {
6704                     parser->toksuper = i;
6705                     break;
6706                 }
6707             }
6708 #endif
6709             break;
6710         case '\"':
6711             r = jsmn_parse_string(parser, js, len, tokens, num_tokens);
6712             if (r < 0) return r;
6713             count++;
6714             if (parser->toksuper != -1 && tokens != NULL)
6715                 tokens[parser->toksuper].size++;
6716             break;
6717         case '\t' : case '\r' : case '\n' : case ' ':
6718             break;
6719         case ':':
6720             parser->toksuper = parser->toknext - 1;
6721             break;
6722         case ',':
6723             if (tokens != NULL && parser->toksuper != -1 &&
6724                     tokens[parser->toksuper].type != JSMN_ARRAY &&
6725                     tokens[parser->toksuper].type != JSMN_OBJECT) {
6726 #ifdef JSMN_PARENT_LINKS
6727                 parser->toksuper = tokens[parser->toksuper].parent;
6728 #else
6729                 for (i = parser->toknext - 1; i >= 0; i--) {
6730                     if (tokens[i].type == JSMN_ARRAY || tokens[i].type == JSMN_OBJECT) {
6731                         if (tokens[i].start != -1 && tokens[i].end == -1) {
6732                             parser->toksuper = i;
6733                             break;
6734                         }
6735                     }
6736                 }
6737 #endif
6738             }
6739             break;
6740 #ifdef JSMN_STRICT
6741             /* In strict mode primitives are: numbers and booleans */
6742         case '-': case '0': case '1' : case '2': case '3' : case '4':
6743         case '5': case '6': case '7' : case '8': case '9':
6744         case 't': case 'f': case 'n' :
6745             /* And they must not be keys of the object */
6746             if (tokens != NULL && parser->toksuper != -1) {
6747                 jsmntok_t *t = &tokens[parser->toksuper];
6748                 if (t->type == JSMN_OBJECT ||
6749                         (t->type == JSMN_STRING && t->size != 0)) {
6750                     return JSMN_ERROR_INVAL;
6751                 }
6752             }
6753 #else
6754             /* In non-strict mode every unquoted value is a primitive */
6755         default:
6756 #endif
6757             r = jsmn_parse_primitive(parser, js, len, tokens, num_tokens);
6758             if (r < 0) return r;
6759             count++;
6760             if (parser->toksuper != -1 && tokens != NULL)
6761                 tokens[parser->toksuper].size++;
6762             break;
6763 
6764 #ifdef JSMN_STRICT
6765             /* Unexpected char in strict mode */
6766         default:
6767             return JSMN_ERROR_INVAL;
6768 #endif
6769         }
6770     }
6771 
6772     if (tokens != NULL) {
6773         for (i = parser->toknext - 1; i >= 0; i--) {
6774             /* Unmatched opened object or array */
6775             if (tokens[i].start != -1 && tokens[i].end == -1) {
6776                 return JSMN_ERROR_PART;
6777             }
6778         }
6779     }
6780 
6781     return count;
6782 }
6783 
6788 static void jsmn_init(jsmn_parser *parser) {
6789     parser->pos = 0;
6790     parser->toknext = 0;
6791     parser->toksuper = -1;
6792 }
