245 float _glfw_fminf(float a, float b)
246 {
247     if (a != a)
248         return b;
249     else if (b != b)
250         return a;
251     else if (a < b)
252         return a;
253     else
254         return b;
255 }
256 
257 float _glfw_fmaxf(float a, float b)
258 {
259     if (a != a)
260         return b;
261     else if (b != b)
262         return a;
263     else if (a > b)
264         return a;
265     else
266         return b;
267 }
