540 GLFWAPI GLFWerrorfun glfwSetErrorCallback(GLFWerrorfun cbfun)
541 {
542     _GLFW_SWAP(GLFWerrorfun, _glfwErrorCallback, cbfun);
543     return cbfun;
544 }
