70 // Finds a mapping based on joystick GUID
71 //
72 static _GLFWmapping* findMapping(const char* guid)
73 {
74     int i;
75 
76     for (i = 0;  i < _glfw.mappingCount;  i++)
77     {
78         if (strcmp(_glfw.mappings[i].guid, guid) == 0)
79             return _glfw.mappings + i;
80     }
81 
82     return NULL;
83 }
