416 // Notifies shared code of a joystick connection or disconnection
417 //
418 void _glfwInputJoystick(_GLFWjoystick* js, int event)
419 {
420     assert(js != NULL);
421     assert(event == GLFW_CONNECTED || event == GLFW_DISCONNECTED);
