5 // Min comparison between 2 variables
6 template<typename T, typename U, qualifier Q>
7 GLM_FUNC_QUALIFIER U associatedMin(T x, U a, T y, U b)
8 {
9     return x < y ? a : b;
10 }
11 
12 template<length_t L, typename T, typename U, qualifier Q>
13 GLM_FUNC_QUALIFIER vec<2, U, Q> associatedMin
14 (
15     vec<L, T, Q> const& x, vec<L, U, Q> const& a,
16     vec<L, T, Q> const& y, vec<L, U, Q> const& b
17 )
18 {
19     vec<L, U, Q> Result;
20     for(length_t i = 0, n = Result.length(); i < n; ++i)
21         Result[i] = x[i] < y[i] ? a[i] : b[i];
22     return Result;
23 }
