51 // Min comparison between 3 variables
52 template<typename T, typename U>
53 GLM_FUNC_QUALIFIER U associatedMin
54 (
55     T x, U a,
56     T y, U b,
57     T z, U c
58 )
59 {
60     U Result = x < y ? (x < z ? a : c) : (y < z ? b : c);
61     return Result;
62 }
