78 // Min comparison between 4 variables
79 template<typename T, typename U>
80 GLM_FUNC_QUALIFIER U associatedMin
81 (
82     T x, U a,
83     T y, U b,
84     T z, U c,
85     T w, U d
86 )
87 {
88     T Test1 = min(x, y);
89     T Test2 = min(z, w);
90     U Result1 = x < y ? a : b;
91     U Result2 = z < w ? c : d;
92     U Result = Test1 < Test2 ? Result1 : Result2;
93     return Result;
94 }
